                            "galleryOverlay"          : "no",
                            "linkedElement"           : ["all"], // ["image", "title", "overlay", "all"]
                            "playPause"               : "no",
                            "mainControls"            : "no",
                            "contMoreLink"            : "no",
                            "contMoreLinkText"        : "Read More",
                            "descMoreLink"            : "yes",
                            "descMoreLinkText"        : "Read More",
                            "descVideoLink"           : "no",
                            "descVideoLinkText"       : "Watch Video",
                            "bullets"                 : "no",
                            "bulletControls"          : "no",
                            "thumbnails"              : "no",
                            "thumbnailWidth"          : 122,
                            "thumbnailHeight"         : 53,
                            "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                            "hoverPause"              : "yes",
                            "autoRotate"              : "yes",
                            "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
                            "fullScreenBreakpoints"   : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                            "fullScreenVideo"         : "no",
                            "transitionType"          : "fade", // fade or slide
                            "transitionSpeed"         : 3,
                            "transitionDelay"         : 8,
                            "onImageLoad"             : function() { },
                            "allImagesLoaded"         : function() { },
                            "onThumbnailLoad"         : function() { },
                            "allThumbnailsLoaded"     : function() { },
                            "onTransitionStart"       : function() { },
                            "onTransitionEnd"         : function() { },
                            "watchVidClick"           : function() { },
                            "closeVidClick"           : function() { },
                            "allLoaded"               : function() { 
                            							$('.rs-gallery-desc-more-link a span').addClass('cs-accessible-hidden');
                                                        var iconArrow = '';
                                                        $('.rs-gallery-desc-more-link a').prepend(iconArrow);

                                                        if($(window).width() >= 1024 && $(window).width() <= 1238) {
                                                            var windowWidth = $(window).width();
                                                            var slideshowRight = $(".hp-slideshow.right").width();
                                                            var offset = slideshowRight - (windowWidth - 80 - 708)
                                                            $("#hp-slideshow .rs-gallery-container .rs-gallery-desc").css("right",offset);
                                                        } else {
                                                        	$("#hp-slideshow .rs-gallery-container .rs-gallery-desc").css("right","0px");
                                                        }
                                                        $(window).resize(function(){
                                                        	if($(window).width() >= 1024 && $(window).width() <= 1238) {
                                                                var windowWidth = $(window).width();
                                                                var slideshowRight = $(".hp-slideshow.right").width();
                                                                var offset = slideshowRight - (windowWidth - 80 - 708)
                                                                $("#hp-slideshow .rs-gallery-container .rs-gallery-desc").css("right",offset);
                                                            } else {
                                                                $("#hp-slideshow .rs-gallery-container .rs-gallery-desc").css("right","0px");
                                                            }    
                                                        });
                            }

			        });
			}
		});
	}

    function checkSlideshow() {
		if($(".hp").length && csGetBreakPoint() != "desktop") {
			if ($(window).scrollTop() <= $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
				if(csSlideshowDescFixed) {
					$("#hp-slideshow .rs-gallery-desc").removeAttr("style");

					csSlideshowDescFixed = false;
				}
			} else {
				if(!csSlideshowDescFixed) {
					$("#hp-slideshow .rs-gallery-desc").css({
						"height": $("#hp-slideshow .rs-gallery-desc").height(),
						"overflow": "hidden"
					});

					csSlideshowDescFixed = true;
				}
			}
		}
	}

    function appView768(){
		//MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
		/*if(!$("#hp-content div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}*/

		//DONT ADD APP INTO ACCORDIAN IF IT DOESNT HAVE A HEADER
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//ACCORDIAN HEADER CLICK
		$("#hp-tabs div.ui-widget-header").off("click").click(function(){
			if(!$(this).parent().hasClass("active")){

            	$(this).next("div.ui-widget-detail").slideDown("slow");
                $(this).parent().addClass("active");
            } else {
            	$(this).next("div.ui-widget-detail").slideUp("slow");
                $(this).parent().removeClass("active");
            }
		});

        //ACCORDIAN HEADER CLICK
		$("#hp-region-three div.ui-widget-header").off("click").click(function(){
			if(!$(this).parent().hasClass("active")){

            	$(this).next("div.ui-widget-detail").slideDown("slow");
                $(this).parent().addClass("active");
            } else {
            	$(this).next("div.ui-widget-detail").slideUp("slow");
                $(this).parent().removeClass("active");
            }
		});

	}

	function appView(){
		//MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
		/*if(!$("#hp-content div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}*/

		//DONT ADD APP INTO ACCORDIAN IF IT DOESNT HAVE A HEADER
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//ACCORDIAN HEADER CLICK
		$("div.ui-widget-header").not("#hp-region-two div.ui-widget-header").off("click").click(function(){
			if(!$(this).parent().hasClass("active")){

            	$(this).next("div.ui-widget-detail").slideDown("slow");
                $(this).parent().addClass("active");
            } else {
            	$(this).next("div.ui-widget-detail").slideUp("slow");
                $(this).parent().removeClass("active");
            }
		});
	}	

	function tabbedApps(){	
		head.ready("tabBlocks", function() {
			//TABBED APPS
            if(!$("div#hp-tabs div#sw-content-container1 .tabs-container").length){
                $("div#hp-tabs div#sw-content-container1").tabBlocks();
                $("div#hp-tabs div#sw-content-container1 .tab-button:eq(0)").addClass("first");
                $("#sw-content-container1.ui-hp").children().not($(".tabs-container")).wrapAll('
');
			}
            if(!$("#hp-region-one-tabs div#sw-content-container2 .tabs-container").length){
                $("#hp-region-one-tabs div#sw-content-container2").tabBlocks();
                $("#hp-region-one-tabs div#sw-content-container2 .tab-button:eq(0)").addClass("first");
			}
		});	
	}

	function useChannelForSection(){
		//USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
