			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }   

	function dynamicContent(){

		// ********** MYSTART ********** //

		//TO FIX THE ISSUE OF SEARCH NOT ALLOWING SPACES
        $("#sw-mystart-search").unbind();

        //ADD BUTTON ROLE TO SW-MYACCOUNT
        $("#sw-myaccount").attr("role","button");

		//APPEND SEARCH TO GB-SEARCH-BOTTOM
		$("#sw-mystart-search").appendTo("#gb-search");

        //ADD ROLE AND ARIA LABEL TO SEARCH BUTTON
        $("#sw-search-button").attr("role","button").attr("aria-label","Submit Search").html('');

        //ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
		$(".sw-mystart-dropdown").addClass("transition");

        //ADD ARROW CLASS TO ALL MYSTART DROPDOWN ARROW
		$(".sw-dropdown-arrow").addClass("icon-font-arrow");

        //ADD ARROW CLASS TO MYACCOUNT-CHEVRON
		$(".sw-myaccount-chevron").addClass("icon-font-arrow").text("");

        //CHANGE THE SCHOOLLIST TEXT
		$(".sw-mystart-dropdown.schoollist .selector").text("Our Schools");

		//ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
		$(".sw-mystart-button").addClass("transition");

        //APPEND MYSTART ITEMS TO APPROPRIATE PLACES





        //CHANGE TEXT OF SIGNIN BUTTON
        $(".sw-mystart-button.signin a span").text("Log In");

        //DOC ADD CUSTOM CLICK EVENT FOR SCHOOLLIST DROPDOWN
		$(".sw-mystart-dropdown.schoollist").off();
		$(".sw-mystart-dropdown.schoollist").click(function(){
			$(this).toggleClass("open");
			$(".sw-dropdown-arrow",this).toggleClass("open");
			$(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown").attr("aria-hidden") == "true") ? "false" : "true");
		});

		//DOC ADD CUSTOM CLICK EVENT FOR MYACCOUNT DROPDOWN
		$("#sw-mystart-account .sw-dropdown").attr("id","cs-sw-myaccount-list").removeClass("sw-dropdown");
		$("#sw-mystart-account").off();
		$("#sw-mystart-account").click(function(){
			$("#sw-myaccount", this).toggleClass("open");
			$("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");
		});

		// ********** HEADER ********** //

		//ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/NY01001023/Centricity/Template/GlobalAssets/images//logos/PWUFSD_SM.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
                $("#gb-sitename").css("display","inline-block");
			} else {
				$("#gb-logo").append('').removeClass("hidden");
			}
		}

        //ADD SITENAME
        var siteNameOne = jQuery.trim('Port Washington');
        var siteNameTwo = jQuery.trim('Union Free School District');
        if((siteNameOne == "") && (siteNameTwo == "")) {
            var splitLen = 2;
            var siteName = "Port Washington UFSD";
            siteName = siteName.split(" ");
            var siteNameLength = siteName.length;
            if(siteNameLength > 2){
                siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
            } else {
                siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
            }
            siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
            $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
            $("#gb-footer-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
        } else if((siteNameOne != "") && (siteNameTwo == "")) {
            $("#gb-sitename").prepend("
" + siteNameOne + "");
            $("#gb-footer-sitename").prepend("
" + siteNameOne + "");
        } else if((siteNameOne == "") && (siteNameTwo != "")) {
            $("#gb-sitename").prepend("
" + siteNameTwo + "");
            $("#gb-footer-sitename").prepend("
" + siteNameTwo + "");
        } else if((siteNameOne != "") && (siteNameTwo != "")) {
            $("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
            $("#gb-footer-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
        }


        var siteAlias = "portnet";
        if(siteAlias == "portnet") {
            //STYLE FIRST WORD OF SITENAME
            $('#gb-sitename h1, #gb-footer-sitename h2').each(function() {
                var word = $(this).html();
                var index = word.indexOf(' ');
                if(index == -1) {
                    index = word.length;
                }
                $(this).html('' + word.substring(0, index) + '' + word.substring(index, word.length));
            });
        }    


		// ********** CHANNEL BAR ********** //

		//ADD FIRST CLASS TO LAST CHANNEL ITEM
		$("li.sw-channel-item").first().addClass("first");
		//ADD LAST CLASS TO LAST CHANNEL ITEM
		$("li.sw-channel-item").last().addClass("last");


		//ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
		$("ul.sw-channel-list li.sw-channel-item").addClass("transition");

		// ********** BODY ********** //

		//RENAME FIRST BREADCRUMB
		$("li.ui-breadcrumb-first > a > span").text("Home");

		//REPLACE MORE-LINK TEXT
		$("a.more-link").html('VIEW ALL');

        //REPLACE RSS TEXT
        $(".ui-widget.app .app-level-social-rss a").not(".ui-widget.app.calendar .app-level-social-rss a").html('RSS');

		//AUTO-FOCUS UERSNAME SIGIN FIELD
		$("#swsignin-txt-username").focus();

		//CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//ADD SIGNED OUT BREADCRUMB
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}

        //ADD READ FULL STORY BUTTON TO EACH HEADLINE
        $(".ui-widget.app.headlines").each(function(){
            $(".ui-article", this).each(function(){
                var headlineURL = $("h1 a", this).attr("href");
                $('Read More').appendTo($(".ui-article-description", this));
            });
        });

        //ADD SIGN IN BREADCRUMB
        if($("#swlogin").length){
            $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
            $(".ui-breadcrumbs").append("
Sign In");
        }

       	//REGISTER BREADCRUMB
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		//SEARCH RESULTS BREADCRUMB
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}

		//CHANGE TEXT OF CALENDAR LINK
		$("a.view-calendar-link").text("view full calendar");

		// APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        $("div.ui-widget.app .ui-widget-detail img")
            .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
            .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
            .each(function() {
                if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                    $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                }
        });

        //ADD UI-CLEAR CLASS TO HEADLINES APP IN REGION ONE
        //$("#hp-region-one-tabs .ui-widget.app.headlines .ui-widget-detail .ui-articles").addClass("ui-clear");

        //$("#hp-region-one-tabs .ui-widget.app.headlines .ui-widget-detail .ui-articles .more-link-under").prependTo("#hp-region-one-tabs .ui-widget.app.headlines .ui-widget-footer");
        //$("#hp-region-one-tabs .ui-widget.app.headlines .ui-widget-detail .ui-articles .more-link").prependTo("#hp-region-one-tabs .ui-widget.app.headlines .ui-widget-footer");
        //$("#hp-region-one-tabs .ui-widget.app.announcements .ui-widget-detail .ui-articles .more-link-under").prependTo("#hp-region-one-tabs .ui-widget.app.announcements .ui-widget-footer");
       	//$("#hp-region-one-tabs .ui-widget.app.announcements .ui-widget-detail .ui-articles .more-link").prependTo("#hp-region-one-tabs .ui-widget.app.announcements .ui-widget-footer");

        //added by Matt - Case 2974196 - 11.07.17
        $("#hp-region-one-tabs .ui-widget.app.headlines .ui-widget-detail .ui-articles li").each(function(){
        	var moreLink = $(".more-link",this);
            var moreLinkUnder = $(".more-link-under",this);
        	if(moreLink.length > 0){
                $(this).parent().parent().next(".ui-widget-footer").prepend(moreLinkUnder);
                $(this).parent().parent().next(".ui-widget-footer").prepend(moreLink);
                $(this).remove();
        	}
        });
        $("#hp-region-one-tabs .ui-widget.app.announcements .ui-widget-detail .ui-articles li").each(function(){
        	var moreLink = $(".more-link",this);
            var moreLinkUnder = $(".more-link-under",this);
        	if(moreLink.length > 0){
                $(this).parent().parent().next(".ui-widget-footer").prepend(moreLinkUnder);
                $(this).parent().parent().next(".ui-widget-footer").prepend(moreLink);
                $(this).remove();
        	}
        });

        $("#hp-region-two .ui-widget.app.upcomingevents .ui-widget-detail .view-calendar-link").prependTo("#hp-region-two .ui-widget.app.upcomingevents .ui-widget-footer");


        //DOC add span to content lists
        $(".ui-article ul li, .ui-article ol li").wrapInner("");

        $("#hp-region-three .ui-widget.app .ui-widget-header h1").each(function(){
        	var headerText = $(this).text().toLowerCase();
        	if(headerText == "facebook") {
            	$(this).addClass("facebook");
            } else if(headerText == "twitter") {
            	$(this).addClass("twitter");
            } else if(headerText == "instagram") {
            	$(this).addClass("instagram");
            } 
        });

		// ********** FOOTER ********** //
		//PHONE AND FAX NUMBERS
		if("516-767-5000" != "") {
			$(".gb-footer-contact.phone").removeClass("hidden");
		}
