		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");
		var swLogo = $("#sw-footer-logo a img").attr("src");
		var swURL = $("#sw-footer-logo a").attr("href");

		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);	
		$("#sw-logo a").attr("href", swURL);	
        $("#sw-logo a img").attr("src", swLogo);
	}

    function stickyCalendarFix() {
        var channelHeight = $("#gb-channel-list-outer").height();

        if ($(window).scrollTop() <= channelHeight) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", channelHeight + "px");
                }
            }
        }
    }

	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "SEARCH");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "SEARCH") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("SEARCH");
			}
		});
	}


	function modEvents(){
		head.ready("modEvents", function() {
			if (!$(".upcomingevents .joel-month").length) {
				$(".upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "no"
				});
				eventsByDay(".upcomingevents .ui-articles");
			}
		});
	}		

	function eventsByDay(container) {
		//MOVE ALL EVENTS WITH THE SAME DATES INTO THE SAME CONTAINER
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});

        $(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				newDate += newDateTime[0] + "";
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	
		});


        $(".ui-widget.app.upcomingevents li .ui-article-description").each(function() {
          		// CHECK FOR TIME
            if($(".sw-calendar-block-time", this).length) {
            	$(this).append($(".sw-calendar-block-time", this));
            } else {
            	$(this).append('ALL DAY');
            }
          });

	}

	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

    function accessibility() {
        $(".sw-mystart-dropdown").each(function(i) {
            // SET ATTRIBUTES
            $(this).attr("role","button");
            $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1));
            $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id"));
            $(".sw-dropdown", this).attr("aria-hidden", "true");
            $(".sw-dropdown-list li", this).attr("role", "menuitem");
            $(".sw-dropdown-list li a", this).attr("tabindex", "0");
        });

        $("#sw-search-button").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.space:
                	e.preventDefault();
                    SWGoToSearchResultsPageswsearchinput();

                break;
            }
        });

        $(".sw-mystart-dropdown").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();
                    $(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");

                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                      if(!$(this).hasClass("open")) {
                          // TOGGLE THE DROPDOWN
                          $(this).addClass("open");
                          $(".sw-dropdown-list li:first-child a", this).focus();
                      } else {
                      		$(this).removeClass("open");
                      }
                      $(".sw-dropdown-list li a", this).attr("tabindex","0");
                    // FOCUS FIRST ITEM

                break;
            }
        });

        $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                break;
            }
        });

        $("#cs-sw-myaccount-list li").each(function(){
            $("> a", this).attr("tabindex", "0"); 
        }); 

        $("#sw-myaccount").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();


                    $(this).next("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");


                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                 	if(!$(this).hasClass("open")) {
                          // TOGGLE THE DROPDOWN
                     	$(this).addClass("open");
                      	$(this).next("#cs-sw-myaccount-list").find("li:first-child a").focus();
                    } else {
                      	$(this).removeClass("open");
                    }
                    // FOCUS FIRST ITEM

                break;
            }
        });    

        $("#cs-sw-myaccount-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                break;
            }
        });

    }

    var parallaxScroll = function(){
		if ($(window).scrollTop() <= 150) {
			$("#gb-channel-list").removeClass("channel-drop-down");
		} else {
			$("#gb-channel-list").addClass("channel-drop-down");
		}
        var mystartHeight = $("#sw-mystart-outer").height();
        var headerHeight = $("header").height();
        if($(".hp").length){
        	var scrollDistance = mystartHeight + headerHeight + 43;
        } else {
        	var scrollDistance = mystartHeight + headerHeight;
        }
        var channelHeight = $("#gb-channel-list-outer").height();
        if ($(window).scrollTop() <= scrollDistance) {
			$("#gb-channel-list-outer").removeClass("fixed");
            $("#hp-region-one-outer").css("margin-top","0px");
            $("#sp-content-outer").css("margin-top","0px");
            $("#spn-content-outer").css("margin-top","0px");
		} else {
			$("#gb-channel-list-outer").addClass("fixed");
            $("#hp-region-one-outer").css("margin-top",channelHeight);
            $("#sp-content-outer").css("margin-top",channelHeight);
            $("#spn-content-outer").css("margin-top",channelHeight);
		}
	}

