$(window).load(function() {

		$('#slide-show-loader-770').fadeOut('slow');

		$('#slide-show-770').css({
		'visibility':'visible'
		}).animate({
			'filter': 'alpha(opacity=100)',
			'opacity': '1'
		}, 500);
		

		
		$.fn.fadeInElements = function() {
				$(this).find('.slide-show-overlay').delay(250).fadeIn('slow');
				$(this).find('.slide-show-headlines').delay(350).fadeIn('slow');
				$(this).find('.slide-show-caption').delay(400).fadeIn('slow');
		};

		$.fn.fadeOutElements = function() {
				$(this).find('.slide-show-overlay').hide();
				$(this).find('.slide-show-headlines').hide();
				$(this).find('.slide-show-caption').hide();
		};

		$('#slide-show-770').bxSlider({
			mode: 'horizontal',
			pause: 8000,
			auto: true,
			speed: 1000,
			autoHover: true,
			pagerCustom: '#slide-show-pager-770',
			nextText: '',
			prevText: '',
			nextSelector: '#slide-show-next-770',
			prevSelector: '#slide-show-prev-770',
			
			
			onSlideAfter: function (currentSlideNumber, totalSlideQty, currentSlideHtmlObject) {
				$('#slide-show-770').find('.slide-show-active').removeClass('slide-show-active').fadeOutElements();
									$('ul#slide-show-770 > li').eq(currentSlideHtmlObject + 1).addClass('slide-show-active').fadeInElements();
							},
			onSliderLoad: function () {
								$('ul#slide-show-770 > li').eq(1).addClass('slide-show-active').fadeInElements();
							}
			
		});
		

});



 
	
		function slideshowRefactoring770(){
			
			var SSmaxHeightSetting = '452';
			var aspectRatio = 0.75;
			var slideShow = $('#slide-show-770');
			var slideShowItems = $(slideShow).find('.slide-show-item');
			var SSnewWidth = $('#slide-show-outer-770').outerWidth();
			
			//console.log('new width = '+SSnewWidth);
			
			// find the new height based on ratio
			var SSnewHeight =  SSnewWidth * aspectRatio;
			
			//console.log('calculated new height = '+SSnewHeight);
			
			function ssCheckHeight(){
							
				// if the new height is greater than the original max height, fall back to the max height
				// so we don't end up serving up a ridiculously huge image on a huge monitor.
				// THIS WILL CAUSE CROPPING but it's unavoidable
				
				if (SSnewHeight > SSmaxHeightSetting) {
					$(slideShowItems).css({
						'height':SSmaxHeightSetting+'px'
					});					
				} else {	
					$(slideShowItems).css({
						'height':Math.round(SSnewHeight)+'px'
					});	
				}
			}
			
			ssCheckHeight();
					
			// Headline Centering / Middling
			$('.slide-show-headlines').each(function(){
				
				var headlineOverlayHtAdjust = $(this).height()/2;
				
				$(this).css({
				 'marginTop':-headlineOverlayHtAdjust+'px'
				});
			
			});
								
		}
	
		slideshowRefactoring770();
		
		$(window).on('resize', function () {
			slideshowRefactoring770();
		}).resize();

