link.script:
		$(document).ready(function(e) {
            //setInterval(swapBgImg,15000);
			$('#foo5').carouFredSel({
				width: '100%',
				scroll: {duration: 1500}
			});
			//$('#foo6').carouFredSel({
			//	width: '100%',
			//	scroll: 750
			//});
			$('.showRhide').bind('click', function(ev){
				ev.stopPropagation();
				var tsp = $(this).children('span').eq(0).html();
				if (tsp == '+') {
					$(this).children('span').eq(0).html('-');
				} else {
					$(this).children('span').eq(0).html('+');
				}
				var targ = $(this).attr('data-tar');
				$('.'+targ).toggle('show');
			});
			$('#GIEpict').bind('mouseover', function () {
   this.src= "assets/images/GIE2018110102.png";
}).bind('mouseout', function () {
   this.src= "assets/images/GIE2018110101.png";
});	
        });
