	$(window).load(function(){
		if($(window).width() >= 320 && $(window).width() <= 639){
			calendarListView();
		}
		searchText();

		$("div.ui-widget.app.navigation li div.bullet").click(function(){
			return true;
		});

        //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-mystart-dropdown.schoollist").unbind("hover");
		$(".sw-mystart-dropdown.schoollist").hover(function(){

			},function(){
			$(".sw-dropdown-arrow",this).removeClass("open");
			$(this).removeClass("open");
			$(".sw-dropdown", this).slideUp("slow", "swing");
		});

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-mystart-dropdown.translate").unbind('hover');
		$(".sw-mystart-dropdown.translate").hover(function(e){}, function(e) {
			$(".sw-dropdown-arrow").removeClass("rotate-arrow");
			$(this).removeClass("open");
			$(".sw-dropdown",this).slideUp("slow", "swing").attr("aria-hidden","true");
            $(".selector",this).attr("aria-expanded","false");
		});

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$("#sw-mystart-account").unbind('hover');
		$("#sw-mystart-account").hover(function(){

		}, function() {
            $(this).removeClass("open clicked-state");
            $("#sw-myaccount",this).attr("aria-expanded","false");
            $(".icon-font-arrow",this).removeClass("open");
            $("#cs-sw-myaccount-list").slideUp("slow", "swing").attr("aria-hidden","true");
        });

        //DOC check if audience channel dropdown goes off screen
		$(".call-out-channels .sw-channel-item").on('mouseenter mouseleave', function (e) {
			if ($('ul', this).length) {
				//$(".sw-channel-dropdown", this).show();
				var elm = $('ul:first', this);
				var off = elm.offset();
				var l = off.left;
				var w = elm.width();
				var docH = $(".sw-channel-list").height();
				var docW = $(".sw-channel-list").width();
                var winW = $(window).width();
                //var overflow = -((l + w) - winW + 80);
                var overflow = l;

				//var isEntirelyVisible = (l + w <= winW);
				//if (!isEntirelyVisible) {
                if(l < 0){
					$(this).addClass('edge');
                    $(".mega-channel.sw-channel-item.edge ul.sw-channel-dropdown").css("right", overflow);
				} else {
					$(this).removeClass('edge');
				}
			}
		});

        //DOC check if main channel dropdown goes off screen
		$("#gb-channel-list .sw-channel-item").on('mouseenter mouseleave', function (e) {
			if ($('ul', this).length) {
				//$(".sw-channel-dropdown", this).show();
				var elm = $('ul:first', this);
				var off = elm.offset();
				var l = off.left;
				var w = elm.width();
				var docH = $("#sw-channel-list-container").height();
				var docW = $("#sw-channel-list-container").width();
                var winW = $(window).width();
                var overflow = -((l + w) - winW + 20);

				var isEntirelyVisible = (l + w <= winW);
				if (!isEntirelyVisible) {
					$(this).addClass('edge');
                    $("#gb-channel-list .mega-channel.sw-channel-item.edge ul.sw-channel-dropdown").css("left", overflow);
				} else {
					$(this).removeClass('edge');
				}
			}
		});

        //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
            $("main").addClass("overlay");
            $("footer").addClass("overlay");
		}, function(){
			$(".sw-channel-item ul").stop(true, true);
            $("main").removeClass("overlay");
            $("footer").removeClass("overlay");
		});

        logoWidth480();

        $("#gb-mystart-inner .sw-mystart-dropdown.translate").removeAttr("tabindex");
        $("#gb-mystart-inner .sw-mystart-dropdown.translate .selector").attr("tabindex", "0");

        $('.site-shortcuts li.expandable').unbind('click').click(function(){
        	if($(this).hasClass('open')){

    						$(this).removeClass('open');
    						$('ul', this).slideUp();
            } else {
            	$(this).addClass('open');
    						$('ul', this).slideDown();
            }
        });
        $('.page-navigation li.expandable').unbind('click').click(function(){
        	if($(this).hasClass('open')){

    						$(this).removeClass('open');
    						$('ul', this).slideUp();
            } else {
            	$(this).addClass('open');
    						$('ul', this).slideDown();
            }
        });	

        // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();
	});

	var CreativeTemplateMods = {
    	//05.02.19 by Paul N.
        //Design by Kimber F.

        // PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },

        //Methods
		"Init": function() {
        	/*if($(".sw-special-mode-bar").length || $('#templateconfiguration-iframe-preview', window.parent.document).length) {
            	$("#gb-page").addClass("preview-changes");
            }*/

            this.HeadlinesReadMore();
            this.StreamingVideo();
            this.JSMediaQueries();
        },

        "HeadlinesReadMore": function() {
        	$(".headlines .ui-article").each(function() {
            	$(".ui-article-description", this).html($.trim($(".ui-article-description", this).text())+" ");
            	$(".ui-article-description", this).append('Read More');
            });
        },

        "StreamingVideo": function() {
        	var _this = this;

        	if(false) {
            	//Variables
                var mobilePhoto = '/cms/lib/NY01814060/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg';
                var showMobilePhoto = false;

                if(showMobilePhoto) {
                	var photoBreakpoint = 768;
                } else {
                	var photoBreakpoint = 0;
                }
                $("html").addClass("cs-streaming-video");

                if($("#sw-content-container10 .ui-widget.app.cs-rs-multimedia-rotator").length) {
                	//$("#sw-content-container10 > div:not(#cs-fullscreen-video-outer)").hide().attr("aria-hidden", true);
                    $("#sw-content-container10 > div:not(#cs-fullscreen-video-outer)").remove();
                }

                if($.trim(mobilePhoto) == "" || mobilePhoto.indexOf("default-man.jpg") > -1) {
                	mobilePhoto = "/cms/lib/NY01814060/Centricity/Template/16/mobile-photo.jpg";
                }

                var videoVendor = 'Vimeo';
                videoVendor = videoVendor.toLowerCase();

                $("#sw-content-container10.region.ui-hp").fullScreenRotator({
                    "videoSource" : videoVendor, // OPTIONS ARE: youtube, vimeo
                    "videoID": '381360994', // YouTube and Vimeo ids are set as default in the script
                    "showControls" : true,
                    "playAudio": true,
                    "mobileBackgroundPhoto" : mobilePhoto,
                    "mobileBackgroundPhotoBreakpoint" : photoBreakpoint, // OPTIONS ARE 768, 640, 480, 320
                    "onReady" : function(props) {                        
                    	// ADD POSITIONING STYLES
                        var dynStyleSheet = document.createElement('style');
                        if(dynStyleSheet) {
                            dynStyleSheet.setAttribute('type', 'text/css');
                            var head = document.getElementsByTagName('head')[0];

                            if(head) {
                                head.insertBefore(dynStyleSheet, head.childNodes[0]);
                            }

                            var dynStyles = 'iframe#cs-fullscreen-video {' +
                                                'top: calc(((100vw * .4) - (100vw * ' + (props.height / props.width) + ')) / 2);' +	
                                            '}';

                            var rules = document.createTextNode(dynStyles);

                            if(dynStyleSheet.styleSheet){ // IE
                                dynStyleSheet.styleSheet.cssText = dynStyles;
                            } else {
                                dynStyleSheet.appendChild(rules);
                            }
                        }

                        var sbvStructure = "";

                        sbvStructure += '


';


                        if('' != "") {
            				sbvStructure += '
';
						}

                        if('' != "") {         
            				sbvStructure += '
';
						}

                        sbvStructure += '';

                        if('' != "") {         
            				sbvStructure += '
Learn More';
						}

                        sbvStructure += '';

                        $("#cs-fullscreen-video-outer").append(sbvStructure);

                        $("#cs-fullscreen-video-outer .cs-fullscreen-video-buttons").addClass("hide768").appendTo($("#cs-fullscreen-video-outer .mmg-description-inner"));

                        // ADD ARIA LABELS TO BUTTONS
                        $("#hp-slideshow .cs-fullscreen-video-button.mute-button").attr("aria-label", "Mute Background Video");
                        $("#hp-slideshow .cs-fullscreen-video-button.unmute-button").attr("aria-label", "Unmute Background Video");
                        $("#hp-slideshow .cs-fullscreen-video-button.play-button").attr("aria-label", "Play Background Video");
                        $("#hp-slideshow .cs-fullscreen-video-button.pause-button").attr("aria-label", "Pause Background Video");

                        // BUTTON ACTIONS
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.play-button", function(e) {
                        	if(CreativeTemplateMods.allyClick(e)) {
                            	setTimeout(function() {
                                	$("#hp-slideshow .cs-fullscreen-video-button.pause-button").focus();
                                }, 100);
                            }
                        });
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.pause-button", function(e) {
                        	if(CreativeTemplateMods.allyClick(e)) {
                            	setTimeout(function() {
                                	$("#hp-slideshow .cs-fullscreen-video-button.play-button").focus();
                                }, 100);
                            }
                        });
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.mute-button", function(e) {
                        	if(CreativeTemplateMods.allyClick(e)) {
                            	setTimeout(function() {
                                	$("#hp-slideshow .cs-fullscreen-video-button.unmute-button").focus();
                                }, 100);
                            }
                        });
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.unmute-button", function(e) {
                        	if(CreativeTemplateMods.allyClick(e)) {
                            	setTimeout(function() {
                                	$("#hp-slideshow .cs-fullscreen-video-button.mute-button").focus();
                                }, 100);
                            }
                        });
                    },
                    "onStateChange" : function(props) { }
                });

                // MOVE BUTTONS
                $("#hp-slideshow #cs-fullscreen-video-outer").append($("#hp-slideshow .cs-fullscreen-video-buttons"));
            }
        },

        "JSMediaQueries": function() {
            switch(csGetBreakPoint()) {
                case "desktop": 
                    mediaRotator();
                    $(".hp.column.one .ui-widget.app.upcomingevents").each(function(){
                        $(".view-calendar-link",this).appendTo(".hp.column.one");
                    });
                    videoResize(378,193);

                    $(".sp.column.one .column-inner .ui-widget.app .ui-widget-detail, .sp.column.one .column-inner .ui-widget.app .ui-widget-footer").removeAttr("aria-hidden").show();            
                break;
                case "768":
                    mediaRotator();
                    $(".hp.column.one .ui-widget.app.upcomingevents").each(function(){
                        $(".view-calendar-link",this).appendTo(".hp.column.one");
                    });
                    appView();
                    logoWidth();
                    videoResize(240,123);

                    $(".sp.column.one .column-inner .ui-widget.app .ui-widget-detail, .sp.column.one .column-inner .ui-widget.app .ui-widget-footer").removeAttr("aria-hidden").show();            
                break;
                case "640":
                    mediaRotator();
                    $(".hp.column.one .ui-widget.app.upcomingevents").each(function(){
                        $(".view-calendar-link",this).appendTo(".hp.column.one");
                    });
                    appView();
                    logoWidth();
                    videoResize(240,123);

                    $(".sp.column.one .column-inner .ui-widget.app .ui-widget-detail, .sp.column.one .column-inner .ui-widget.app .ui-widget-footer").removeAttr("aria-hidden").show();
                break;
                case "480":
                    mediaRotator();
                    appView480();
                    videoResize(378,193);

                    if(!$("#sp-page-nav-button").length) {
                        $(".sp.column.one .column-inner .ui-widget.app .ui-widget-header").prepend('
PAGES');
                        $(".sp.column.one .column-inner .ui-widget.app .ui-widget-detail, .sp.column.one .column-inner .ui-widget.app .ui-widget-footer").attr("aria-hidden", true).hide();

                        $("#sp-page-nav-button").on("click keydown", function(e) {
                        	e.preventDefault();
                            if($(".sp.column.one .column-inner .ui-widget.app").hasClass("open")) {
                                $(".sp.column.one .column-inner .ui-widget.app").removeClass("open");
                                $(".sp.column.one .column-inner .ui-widget.app .ui-widget-detail").attr("aria-hidden", true).slideToggle("slow");
                            } else {
                                $(".sp.column.one .column-inner .ui-widget.app").addClass("open");
                                $(".sp.column.one .column-inner .ui-widget.app .ui-widget-detail").attr("aria-hidden", false).slideToggle("slow");
                            }
                        });
                    }
                break;
                case "320":
                    mediaRotator();
                    appView480();
                    videoResize(259,134);

                    if(!$("#sp-page-nav-button").length) {
                        $(".sp.column.one .column-inner .ui-widget.app .ui-widget-header").prepend('
PAGES');
                        $(".sp.column.one .column-inner .ui-widget.app .ui-widget-detail, .sp.column.one .column-inner .ui-widget.app .ui-widget-footer").attr("aria-hidden", true).hide();

                        $("#sp-page-nav-button").on("click keydown", function(e) {
                        	e.preventDefault();
                            if($(".sp.column.one .column-inner .ui-widget.app").hasClass("open")) {
                            	$(this).attr("aria-expanded", false);
                                $(".sp.column.one .column-inner .ui-widget.app").removeClass("open");
                                $(".sp.column.one .column-inner .ui-widget.app .ui-widget-detail").attr("aria-hidden", true).slideToggle("slow");
                            } else {
                            	$(this).attr("aria-expanded", true);
                                $(".sp.column.one .column-inner .ui-widget.app").addClass("open");
                                $(".sp.column.one .column-inner .ui-widget.app .ui-widget-detail").attr("aria-hidden", false).slideToggle("slow");
                            }
                        });
                    }               
                break;
            }
        },

        "allyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        }
    };

	$(function() {
    		CreativeTemplateMods.Init();

            // ******************** LOAD CANVAS ON DOC.READY FOR ALL OTHER BROWSERS ******************** //
            initCanvas();

            // ******************** INIT AND DRAW FUNCTIONS ******************** //
            function initCanvas() {
            		//Variables
                    var canvas1 = document.getElementById("canvas1");
                    var canvas2 = document.getElementById("canvas2");
                    var canvas3 = document.getElementById("canvas3");
                    var canvas4 = document.getElementById("canvas4");
                    var canvas5 = document.getElementById("canvas5");
                    var canvas6 = document.getElementById("canvas6");

                    //IF IE
                    if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas1);}
                    var ctx1 = canvas1.getContext("2d");
                    if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas2);}
                    var ctx2 = canvas2.getContext("2d");
                    if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas3);}
                    var ctx3 = canvas3.getContext("2d");
                    if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas4);}
                    var ctx4 = canvas4.getContext("2d");
                    if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas5);}
                    var ctx5 = canvas5.getContext("2d");
                    if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas6);}
                    var ctx6 = canvas6.getContext("2d");

                    drawCanvas1(ctx1);
                    drawCanvas2(ctx2);
                    drawCanvas3(ctx3);
                    drawCanvas4(ctx4);
                    drawCanvas5(ctx5);
                    drawCanvas6(ctx6);
            }

            function drawCanvas1(ctx1) {

                // hP/title
                ctx1.save();

                // hP/title/
                ctx1.save();

                // hP/Path
                ctx1.restore();
                ctx1.beginPath();
                ctx1.moveTo(498.8, 162.4);
                ctx1.lineTo(498.8, 0.0);
                ctx1.lineTo(481.4, 0.0);
                ctx1.bezierCurveTo(395.6, 3.5, 313.2, 31.3, 243.6, 81.2);
                ctx1.bezierCurveTo(171.7, 131.1, 87.0, 158.9, 0.0, 162.4);
                ctx1.lineTo(0.0, 162.4);
                ctx1.lineTo(498.8, 162.4);
                ctx1.closePath();
                ctx1.fillStyle = "rgba(0,0,0,0.35)";
                ctx1.fill();

                // hP/Path
                ctx1.beginPath();
                ctx1.moveTo(0.0, 0.0);
                ctx1.lineTo(498.8, 0.0);
                ctx1.lineTo(498.8, 162.4);
                ctx1.lineTo(0.0, 162.4);
                ctx1.lineTo(0.0, 0.0);
                ctx1.closePath();
                ctx1.restore();
            }
