	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
            	mediaRotator();
				$(".hp.column.one .ui-widget.app.upcomingevents").each(function(){
                    $(".view-calendar-link",this).appendTo(".hp.column.one");
                });
                videoResize(378,193);    
			break;
			case "768":
            	mediaRotator();
            	$(".hp.column.one .ui-widget.app.upcomingevents").each(function(){
                    $(".view-calendar-link",this).appendTo(".hp.column.one");
                });
				appView();
                logoWidth();
                videoResize(240,123);     
			break;
			case "640":
            	mediaRotator();
            	$(".hp.column.one .ui-widget.app.upcomingevents").each(function(){
                    $(".view-calendar-link",this).appendTo(".hp.column.one");
                });
				appView();
                logoWidth();
                videoResize(240,123);
			break;
			case "480":
            	mediaRotator();
				appView480();
                videoResize(378,193);              
			break;
			case "320":
            	mediaRotator();
				appView480();
                videoResize(259,134);              
			break;
		}
	}

	function mediaRotator() {
    	if(CreativeTemplateMods.InTCW) {
            var playPause = "yes";
            var mainControls = "yes";
        } else {
            var playPause = "yes";
            var mainControls = "yes";
        }


        var showStreamingVideo = "false";
        if(showStreamingVideo != "true"){

            if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length) {
                  // MULTIMEDIA ROTATOR
                  $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                          "imageWidth"              : 1600,
                          "imageHeight"             : 585,
                          "efficientLoad"           : "no",
                          "appHeader"               : "no",
                          "titles"                  : "yes",
                          "captions"                : "yes",
                          "galleryOverlay"          : "no",
                          "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
                          "playPause"               : playPause,
                          "mainControls"            : mainControls,
                          "contMoreLink"            : "no",
                          "contMoreLinkText"        : "Read More",
                          "descMoreLink"            : "yes",
                          "descMoreLinkText"        : "Learn More",
                          "bullets"                 : "yes",
                          "bulletControls"          : "no",
                          "thumbnails"              : "no",
                          "thumbnailWidth"          : 122,
                          "thumbnailHeight"         : 53,
                          "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                          "hoverPause"              : "yes",
                          "autoRotate"              : "yes",
                          "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
                          "fullScreenBreakpoints"   : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                          "fullScreenVideo"         : "no",
                          "transitionType"          : "fade", // fade or slide
                          "transitionSpeed"         : 3,
                          "transitionDelay"         : 8,
                          "onImageLoad"             : function() { },
                          "allImagesLoaded"         : function() { },
                          "onThumbnailLoad"         : function() { },
                          "allThumbnailsLoaded"     : function() { },
                          "onTransitionStart"       : function() { },
                          "onTransitionEnd"         : function() { },
                          "watchVidClick"           : function() { },
                          "closeVidClick"           : function() { },
                          "allLoaded"               : function() { 
                              var getWidth = $(window).width();
                              var contentWidth = $("#gb-mystart-inner").width();
                              var galleryDescOffset = ((getWidth-contentWidth)/2);
                              var galleryDescWidth = $("#hp-slideshow .rs-gallery-container .rs-gallery-desc").width();
                              var overlayOffset = -(contentWidth - galleryDescWidth);

                              $("#hp-slideshow .rs-gallery-title, #gb-page #hp-slideshow .rs-gallery-caption").wrapAll('
');
                              $("#hp-slideshow .rs-gallery-desc-inner, #gb-page #hp-slideshow .rs-gallery-desc-more-link").wrapAll('
');
                              $("#hp-slideshow .rs-gallery-control").wrapAll('
');
                              $("#hp-slideshow .rs-gallery-control.next").appendTo($("#gb-page #hp-slideshow .rs-gallery-controls"));
                              $("#hp-slideshow .rs-gallery-controls").appendTo($("#hp-slideshow .rs-gallery-images"));

                          }
                  });
            }

        }
	}

    function videoResize(w,h) {
        if($("#hp-slideshow-overlay iframe").length){
            var playerSrc = $("#hp-slideshow-overlay iframe").attr("src");
            playerSrc = playerSrc.split("&pWidth=");
            if(playerSrc.length == 2){
               var resizeCheck = playerSrc[1].split("&pHeight=");
                if(w != resizeCheck[0]) {
                  var playerSrcNew = playerSrc[0] + "&pWidth=" + w + "&pHeight=" + h;
                  $("#hp-slideshow-overlay iframe").attr("src", playerSrcNew);
                }
            }
         }  
    }

	function appView(){
		//DONT ADD APP INTO ACCORDIAN IF IT DOESNT HAVE A HEADER
		$("#hp-content-bottom div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//ACCORDIAN HEADER CLICK
		$("#hp-content-bottom div.ui-widget-header").off("click").click(function(){

             	$(this).parent().toggleClass("active");
                $(this).next("div.ui-widget-detail").slideToggle("slow");

		});
	}	

    function appView480(){


		//DONT ADD APP INTO ACCORDIAN IF IT DOESNT HAVE A HEADER
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//ACCORDIAN HEADER CLICK
		$("#hp-content div.ui-widget-header").off("click").click(function(){

            $(this).parent().toggleClass("active");
            $(this).next("div.ui-widget-detail").slideToggle("slow");
		});
	}

    function logoWidth() {
		if($("#gb-logo img").length) {
			$("#gb-logo").css("width","auto");
		}
	}

    function logoWidth480() {
    	var getWidth = $(window).width();
        if (getWidth >= 320 && getWidth < 640) {
          if($("#gb-logo img").length) {
              var imgWidth = $("#gb-logo img").width()*.75;
              $("#gb-logo img").css("width",imgWidth);
          }
        }  
	}

	function useChannelForSection(){
		//USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

    function addColHeadWidth() {
        	var getWidth = $(window).width();
            var contentWidth = $("#gb-school-list").width();
            var addColumnOffset = ((getWidth-contentWidth)/2);
            var addColumnWidth = $("#gb-school-list-two").width();
            var addColumnHeaderWidth = addColumnOffset + addColumnWidth;
            $("#gb-schoollist-add-resources").css("width", addColumnHeaderWidth);
        }    

	function dynamicContent(){
	// ********* ACCESSIBILITY ISSUES CASE 00333100 ADDED BY LYNDSAY ********* //
	$('#events-btn').attr('aria-label', 'events button');
	$('.gb-mystart.left > a').attr('aria-label', 'Yonkers logo link');

    	// ********* EMERGENCY AREA ********* //
        	//HIDE ALERT BOX IF HEADER IS EMPTY
		if($("#hp-emergency li").size()) {
			$("#hp-emergency-outer").removeClass("hidden");
		} else {

		}

    	// ********* SCHOOLIST ********* //

        //BIND THE LIST CLICK EVENT
		$("#gb-schools-btn").click(function(e) {
			e.preventDefault();
			$("#gb-schoollist-outer").toggleClass("open").slideToggle("slow").attr("aria-hidden", ($("#gb-schoollist-outer").attr("aria-hidden") == "true") ? "false" : "true");
            $(this).attr("aria-expanded", ($(this).attr("aria-expanded") == "true") ? "false" : "true");
            if($("#gb-schoollist-outer").hasClass("open")){
            	$(".gb-school-list-header.elem").focus();
            }
            addColHeadWidth();
		});

        $("#gb-schools-btn").keydown(function(e){
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
