	function eventsByDay(container) {
		//MOVE ALL EVENTS WITH THE SAME DATES INTO THE SAME CONTAINER
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});

		$(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				newDate += newDateTime[0] + "";
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}
			});

            //$(".sw-calendar-block-time", container).insertBefore(".sw-calendar-block-title", container);
		});
	}
    function homePageEvents(){
    	// FOR SCOPE
        var template = this;

        var totalEventDays = $("#hp-content-top .upcomingevents li").length;
        var viewCalendarURL = $(".hp.column.one .view-calendar-link").attr("href");

        if(totalEventDays > 4 && $("#hp-content-top .upcomingevents").length){
            //ADD VIEW CALENDAR LINK AND EXPAND EVENTS BUTTON
            $("#hp-content-top").append("

Expand EventsView Full Calendar");

            eventA11y();



            $("#events-btn").click(function(event){

                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();

                    var events = $("#hp-content-top");
                    var clickedButton = $(".btn-text",this);
                    var clickedBtnArrow = $(".icon-font-plus",this);
                    var initialHeight = 345;

                    if(events.hasClass("open")){
                        events.animate({
                            height: initialHeight
                        }, 1000, function() {
                            eventA11y();
                            events.removeClass("open");
                            clickedButton.removeClass("open").text("Expand Events");
                            clickedBtnArrow.removeClass("open");
                        });
                    } else {
                        $("#hp-content-top .upcomingevents .ui-article").css({
                            //"max-height":"none",
                            //"overflow":"visible"
                        })

                        var totalHeight = events.get(0).scrollHeight;

                        events.animate({
                            height: totalHeight
                        }, 1000, function() {
                            events.find("a").attr("tabindex","0");
                            events.addClass("open");
                            clickedButton.addClass("open").text("Collapse Events");
                            clickedBtnArrow.addClass("open");
                        });
                    } 
            }); 

        } else {
            //ADD VIEW CALENDAR LINK
            if($("#hp-content-top .upcomingevents").length){
                $("#hp-content-top").append("

View Full Calendar");
            } else {
                $("#hp-content-top").addClass("no-events");
            }
        }
    }

    function eventA11y(){
        $("#hp-content-top .upcomingevents li").each(function(){
            var elementPosition = $(this).position().top;
            if(elementPosition > 75){

                $(this).find("a").attr("tabindex","-1");
            }

        });
    }

    function allyClickForButtons(event){
          if(event.type === 'click'){
                return true;
            }
            else if(event.type === 'keypress'){
                var code = event.charCode || event.keyCode;
                if(code === 13){
                    return true;
                }
            }
            else{
                return false;
            }
	}



	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

    function schoolListEvents(){
		// TAB HEADER CLICK
		$("#gb-school-list #gb-school-list-one .gb-school-list-header").click(function(e){
			e.preventDefault();
			// IF THE SELECTED TAB ISNT ALREADY ACTIVE
			if(!$(this).hasClass("active")){
				// GET CLASS OF SELECTED TAB
				var selectedList = $(this).attr("class");
				selectedList = jQuery.trim(selectedList.replace("gb-school-list-header ", ""));

				//REMOVE CURRENT ACTIVE CLASSES
				$("#gb-school-list .gb-school-list-header.active, #gb-school-list .gb-school-list-section.active").removeClass("active");


				// SET NEW ACTIVE CLASSES
				$(this).addClass("active");
				$("#gb-school-list .gb-school-list-section." + selectedList).addClass("active");
			}


		});
	}

    function schoollistAddLinks() {

    	var additionalResourcesData = [

        	["Register for School", "/register", "_self"],
            ["Calendars", "/calendars", "_self"],
            ["Forms", "/forms", "_self"],
            ["Menus", "/menus", "_self"],
            ["Transportation", "/transportation", "_self"],
            ["Yonkers Council of PTSAs", "/ycpta", "_blank"]

        ];
        var additionalLinksColumn = "";

        	for(var i = 0; i < 6; i++) {
                if(jQuery.trim(additionalResourcesData[i][0]) != "") {
                    additionalLinksColumn += '
' + additionalResourcesData[i][0] + '';
                }
            }

        $(".gb-school-list-column.additional ul").append(additionalLinksColumn);



    }

     function schoollistAlly(){
    	$("#gb-school-list-headers div[role='tab']").keydown(function(e) { 
            if (e.which ==39) { 
             var selected= $(this).attr("aria-selected"); 
             if (selected =="true"){ 
             	if($(this).is(":last-child")){
                   $("#gb-schools-btn").focus(); 
                   $(this).removeClass("active").attr("aria-selected","false");  
                   $("#gb-school-list-sections div[role='tabpanel']").removeClass("active").attr("aria-hidden","true"); 
                   $("#gb-school-list-sections div[role='tabpanel']:first-child").addClass("active").attr("aria-hidden","false"); 
                   $("#gb-school-list-headers div[role='tab']:first-child").attr("aria-selected","true");
                   $("#gb-school-list-headers #tab1").addClass("active");
                   $("#gb-schoollist-outer").removeClass("open").slideUp("slow").attr("aria-hidden", "true");
            		$("#gb-schools-btn").attr("aria-expanded", "false");

                } else {
                	$(this).next("#gb-school-list-headers div[aria-selected='false']").addClass("active").attr("aria-selected","true").focus() ; 
                   	$(this).removeClass("active").attr("aria-selected","false"); 
                   	var tabpanid= $("#gb-school-list-headers div[aria-selected='true']").attr("aria-controls"); 
                   	var tabpan = $("#gb-school-list-sections #"+tabpanid); 
                   	$("#gb-school-list-sections div[role='tabpanel']").removeClass("active").attr("aria-hidden","true"); 
                   	tabpan.addClass("active").attr("aria-hidden","false");
                }
               }
            }
            if (e.which ==37) { 
             var selected= $(this).attr("aria-selected"); 
             if (selected =="true"){ 
             	if($(this).is(":first-child")){ 
                	 $("#gb-schools-btn").focus(); 
                   $(this).removeClass("active").attr("aria-selected","false"); 
                    $("#gb-school-list-sections div[role='tabpanel']").removeClass("active").attr("aria-hidden","true"); 
                   $("#gb-school-list-sections div[role='tabpanel']:first-child").addClass("active").attr("aria-hidden","false"); 
                   $("#gb-school-list-headers div[role='tab']:first-child").attr("aria-selected","true");
                   $("#gb-school-list-headers #tab1").addClass("active");
                   $("#gb-schoollist-outer").removeClass("open").slideUp("slow").attr("aria-hidden", "true");
            		$("#gb-schools-btn").attr("aria-expanded", "false");
                } else {
                	$(this).prev("#gb-school-list-headers div[aria-selected='false']").addClass("active").attr("aria-selected","true").focus() ; 
                   $(this).removeClass("active").attr("aria-selected","false"); 
                   var tabpanid= $("#gb-school-list-headers div[aria-selected='true']").attr("aria-controls"); 
                   var tabpan = $("#gb-school-list-sections #"+tabpanid); 
                   $("#gb-school-list-sections div[role='tabpanel']").removeClass("active").attr("aria-hidden","true"); 
                   tabpan.addClass("active").attr("aria-hidden","false");
                }
               }
            }
        });

        // SCHOOL TAB KEYDOWN
        $("#gb-school-list-one .gb-school-list-section li a").keydown(function(e) {

            var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

            switch(event.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    event.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().parent().parent().is(":first-child") && $(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(".gb-school-list-column").last().find("li:last-child > a").focus();
                    } else if($(this).parent().is(":first-child")){
                    	$(this).parent().parent().parent().prev(".gb-school-list-column").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }

                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    event.preventDefault();

                    // IS LAST ITEM
                    if($(this).parent().parent().parent().is(":last-child") && $(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $("#gb-school-list-two-sections").find("li:first-child > a").focus();
                    } else if($(this).parent().is(":last-child")){
                    	$(this).parent().parent().parent().next(".gb-school-list-column").find("li:first-child > a").focus();    
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }  

                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    event.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest("ul").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    event.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                break;

                // CONSUME TAB KEY
                case csKeyCodes.tab:
                    event.preventDefault();
                    $("#gb-school-list-headers .gb-school-list-header.active").focus();

                break;

            }

        });

        // SCHOOL TAB KEYDOWN
        $("#gb-school-list-two .gb-school-list-section li a").keydown(function(e) {

            var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

            switch(event.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    event.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $("#gb-school-list-one .gb-school-list-section.active .gb-school-list-column").last().find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }

                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    event.preventDefault();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $("#gb-school-list-one .gb-school-list-section.active .gb-school-list-column").first().find("li:first-child > a").focus();   
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }  

                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    event.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest("ul").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    event.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                break;

                // CONSUME TAB KEY
                case csKeyCodes.tab:
                    event.preventDefault();
                    $("#gb-school-list-headers .gb-school-list-header.active").focus();

                break;

            }

        });

    }

