        $(".call-out-channels li.sw-channel-item").each(function(){

        	// LOWERCASE THE CHANNEL NAME
			var channelText = $("> a span", this).text().toLowerCase();
            var channelTextUpper = $("> a span", this).text();

            //IGNORE HOME AND CALENDAR CHANNELS
                if(channelText != "home" && channelText != "calendar") {
                    // SET THE CHANNELS INDEX
                    var i = ($("#sw-channel-list-container li.sw-channel-item.home").length) ? $(this).index() - 1 : $(this).index();

                    // ADD MEGA CHANNEL CLASS
                    $(this).addClass("mega-channel");

                    // ADD STRUCTURE TO THE DOM
                    $(".sw-channel-dropdown", this).removeAttr("class").wrap('

');


                    $(".mega-column.three",this).prepend('
' + channelTextUpper + '');

                    // ADD ADDITIONAL LINKS
                    var linkColumn = '';
                    if(jQuery.trim(megaMenuData[i].channelLinksHeader) != "") {
                    	linkColumn = '

' + jQuery.trim(megaMenuData[i].channelLinksHeader) + ''
                    }
                    linkColumn += '';
                    var channelLinks = megaMenuData[i].channelLinks;
                    for(var c in channelLinks) {
                        if(jQuery.trim(channelLinks[c].channelLinkText) != "") {
                            linkColumn += '
' + jQuery.trim(channelLinks[c].channelLinkText) + '';
                        }
                    }
                    linkColumn += "";
                    $(".sw-channel-dropdown .mega-columns", this).prepend(linkColumn);

                    // ADD IMAGE, CAPTION AND LINK
                    var imgColumn = '';
                    var imgToggle = jQuery.trim(megaMenuData[i].channelImgToggle);
                    var imgSrc = jQuery.trim(megaMenuData[i].channelImg);
                    var srcSplit = imgSrc.split("/");
                    var srcSplitLen = srcSplit.length;
                    var channelCaptionLink = megaMenuData[i].channelCaptionLink;
                    imgColumn = '
';
                    if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg" && imgToggle !="false") {
                        imgColumn += '';
                    }
                    if(jQuery.trim(megaMenuData[i].channelCaption) != "") {
                    	imgColumn += '
' + jQuery.trim(megaMenuData[i].channelCaption) + ''
                    }
                    for (l in channelCaptionLink){
                    	if(jQuery.trim(channelCaptionLink[l].channelCaptionLinkText) != "") {
                        	imgColumn += '' + jQuery.trim(channelCaptionLink[l].channelCaptionLinkText) + '';
                    	}
                    }    


                    imgColumn += '';
                    $(".sw-channel-dropdown .mega-columns", this).prepend(imgColumn);
                }

            if($("ul.sw-channel-dropdown li.mega-columns > ul", this).html() == ""){
                $("ul.sw-channel-dropdown li.mega-columns > ul", this).hide();
            }   
      	}); 

