    	$("#sw-channel-list-container li.sw-channel-item").each(function(){
            // LOWERCASE THE CHANNEL NAME
            var channelText = $("> a span", this).text().toLowerCase();
            var channelTextUpper = $("> a span", this).text();

            // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
            if(channelText == "home") {
                $(this).addClass("home");
            } else if(channelText == "calendar") {
                $(this).addClass("calendar");
            }

            //IGNORE HOME AND CALENDAR CHANNELS
                if(channelText != "home" && channelText != "calendar") {
                    // SET THE CHANNELS INDEX
                    var i = ($("#sw-channel-list-container li.sw-channel-item.home").length) ? $(this).index() - 1 : $(this).index();

                    // ADD MEGA CHANNEL CLASS
                    $(this).addClass("mega-channel");

                    // ADD STRUCTURE TO THE DOM
                    $(".sw-channel-dropdown", this).removeAttr("class").wrap('

');


                    $(".mega-column.three",this).prepend('
' + channelTextUpper + '');
                    // ADD ADDITIONAL LINKS
                    var linkColumn = '';
                    if(jQuery.trim(mainmegaMenuData[i].mainchannelLinksHeader) != "") {
                    	linkColumn = '

' + jQuery.trim(mainmegaMenuData[i].mainchannelLinksHeader) + ''
                    }
                    linkColumn += '';
                    var channelLinks = mainmegaMenuData[i].mainchannelLinks;
                    for(var c in channelLinks) {
                        if(jQuery.trim(channelLinks[c].mainchannelLinkText) != "") {
                            linkColumn += '
' + jQuery.trim(channelLinks[c].mainchannelLinkText) + '';
                        }
                    }
                    linkColumn += "";
                    $(".sw-channel-dropdown .mega-columns", this).prepend(linkColumn);

                    // ADD IMAGE, CAPTION AND LINK
                    var imgColumn = '';
                    var mainImgToggle = jQuery.trim(mainmegaMenuData[i].mainchannelImgToggle);
                    var imgSrc = jQuery.trim(mainmegaMenuData[i].mainchannelImg);
                    var srcSplit = imgSrc.split("/");
                    var srcSplitLen = srcSplit.length;
                    var channelCaptionLink = mainmegaMenuData[i].mainchannelCaptionLink;
                    imgColumn = '
';
                    if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg" && mainImgToggle !="false") {
                        imgColumn += '';
                    }
                    if(jQuery.trim(mainmegaMenuData[i].mainChannelCaption) != ''){
                    	imgColumn += '
' + jQuery.trim(mainmegaMenuData[i].mainChannelCaption) + ''
                    }
                    for (l in channelCaptionLink){
                    	if(jQuery.trim(channelCaptionLink[l].mainchannelCaptionLinkText) != "") {
                        	imgColumn += '' + jQuery.trim(channelCaptionLink[l].mainchannelCaptionLinkText) + '';
                    	}
                    } 

                    imgColumn += '';
                    $(".sw-channel-dropdown .mega-columns", this).prepend(imgColumn);

                    //IF COLUMN ONE IS EMPTY THEN REMOVE IT
                    $(".mega-column.one:empty").remove();

                    //IF COLUMN ONE AND COLUMN TWO ARE EMPTY THEN HIDE THE CHANNEL NAME AT TOP OF SECTION LIST
                    if(!$(".mega-column.one",this).length && !$(".mega-column.two",this).length && $(".mega-column.three h1",this).text() == "" ){
                    	$(".mega-column.three h1",this).hide();
                    }
                }

            if($("ul.sw-channel-dropdown li.mega-columns > ul", this).html() == ""){
                $("ul.sw-channel-dropdown li.mega-columns > ul", this).hide();
            }     

      	}); 

        // SCHOOL TAB KEYDOWN
        $(".sw-channel-list .mega-channel > a").keydown(function(e) {

            var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

            switch(event.keyCode){

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.down:
                    event.preventDefault();

                    // IS LAST ITEM
                    $(this).parent().find(".mega-column.two ul li:first-child a").focus();

                break;

            }

        });

        // SCHOOL TAB KEYDOWN
        $(".sw-channel-list .mega-channel .mega-columns li a").keydown(function(e) {

            var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

            switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child") && $(this).parent().parent().parent().is(".mega-column.two")) {
                            // FOCUS LAST ITEM
                            $(this).parent().parent().parent().next(".mega-column").find("li:last-child > a").focus();
                        } else if($(this).parent().is(":first-child") && $(this).parent().parent().parent().is(".mega-column.three")) {
                        	$(this).parent().parent().parent().prev(".mega-column").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child") && $(this).parent().parent().parent().is(".mega-column.three")) {
                            // FOCUS FIRST ITEM
                            $(this).parent().parent().parent().prev(".mega-column").find("li:first-child > a").focus();
                        } else if($(this).parent().is(":last-child") && $(this).parent().parent().parent().is(".mega-column.two")) {
                        	$(this).parent().parent().parent().next(".mega-column").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.tab:
                        e.preventDefault();
                        $(".sw-channel-list .mega-channel.hover > a").focus();

                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".mega-column.two li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".mega-column.two li:last-child > a").focus();
                    break;
                }

        });

    }

    var parallaxScrollSticky = function(){
		var getWidth = $(window).width();
        var emergencyHeight =  ($("#hp-emergency-outer").height());
        var schoollistHeight =  ($("#gb-schoollist-outer.open").height());
        var gbMystartHeight = ($("#gb-mystart-outer").height());
		var gbHeaderHeight = ($("#gb-header-outer").height());
        var scrollHeight = emergencyHeight + schoollistHeight + gbMystartHeight + gbHeaderHeight;
		var channelHeight = $("#gb-channel-list-outer").height();

		if($(window).scrollTop() >= scrollHeight) {
			$("#gb-channel-list-outer").addClass("fixed");
			$("main").css("margin-top", channelHeight);
		} else {
			$("#gb-channel-list-outer").removeClass("fixed");
			$("main").css("margin-top", "auto");
		}
	}

	function buildStyleSheet(){
    	var secondaryColor0 = "rgba(" + hexToRgb('#6f7780') + ", .0)";
		var secondaryColor90 = "rgba(" + hexToRgb('#6f7780') + ", .9)";
        var secondaryColor95 = "rgba(" + hexToRgb('#6f7780') + ", .95)";

		var secondaryColor = "#6f7780";
		secondaryColor = secondaryColor.replace('#', '');

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles =	'#hp-content-stats-outer:before {' +
					'	background: ' + secondaryColor90 + ';' +
                    '}' +
                    '#hp-content-top-outer:after {' +
                    '	background : -moz-linear-gradient(50% -1.71% -90deg,' + secondaryColor0 + ' 0%,' + secondaryColor95 + ');' +
  					'	background : -webkit-linear-gradient(-90deg, ' + secondaryColor0 + ' 0%, ' + secondaryColor95 + ' 59.37%);' +
  					'	background : -webkit-gradient(linear,50% -1.71% ,50% 105.06% ,color-stop(0,' + secondaryColor0 + ' ),color-stop(0.5937,' + secondaryColor95 + ' ));' +
  					'	background : -o-linear-gradient(-90deg, ' + secondaryColor0 + ' 0%, ' + secondaryColor95 + ' 59.37%);' +
  					'	background : -ms-linear-gradient(-90deg, ' + secondaryColor0 + ' 0%, ' + secondaryColor95 + ' 59.37%);' +
  					'	-ms-filter: "progid:DXImageTransform.Microsoft.gradient(startColorstr="#6F7780", endColorstr="#6F7780" ,GradientType=0)";' +
  					'	background : linear-gradient(180deg, ' + secondaryColor0 + ' 0%, ' + secondaryColor95 + ' 59.37%);' +
  					'	filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#6F7780",endColorstr="#6F7780" , GradientType=0);' +
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

    var parallaxScroll = function(){
		var getWidth = $(window).width();
		var getHeight = $(window).height();
        var scrollAppear = getHeight * .75;
		if (getWidth < 1024) { // 960+
			if($(window).scrollTop() > scrollAppear) {

            	$("#scroll-to-top").css("opacity","1");
                $("#gb-scroll-to-top-container-outer").addClass("sticky");

			} else {
				$("#scroll-to-top").css("opacity","0");  
                $("#gb-scroll-to-top-container-outer").removeClass("sticky");
			}

		}
	}
    function scrollBtn() {
    	if(!csScrollBtnReset && csGetBreakPoint() == "desktop") {
        	$("#gb-scroll-to-top-container-outer").removeAttr("style");
            csScrollBtnReset = true;
            csScrollBtnIsSticky = false;
            csScrollBtnIsVisible = false;
        } else {
        	if($(".hp").length) {
                if ($(window).scrollTop() < $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
                    if(csScrollBtnIsSticky) {
                        $("#gb-scroll-to-top-container-outer").fadeOut(300, function() {
                            $(this).removeClass("sticky").removeAttr("style");
                        });

                        csScrollBtnIsSticky = false;
                        csScrollBtnReset = false;
                        csScrollBtnIsVisible = false;
                    }
                } else if($(window).scrollTop() >= ($("#hp-slideshow").offset().top + $("#hp-slideshow").height()) && $(window).scrollTop() < (($(".gb.footer.three").offset().top) - $(window).height())) {
                    if(!csScrollBtnIsSticky && csScrollBtnIsVisible || !csScrollBtnIsSticky && !csScrollBtnIsVisible) {
                        $("#gb-scroll-to-top-container-outer").addClass("sticky").fadeIn();

                        csScrollBtnIsSticky = true;
                        csScrollBtnReset = false;
                        csScrollBtnIsVisible = true;
                    }
                } else if($(window).scrollTop() >= (($(".gb.footer.three").offset().top) - $(window).height())) {
                    if(csScrollBtnIsSticky || !csScrollBtnIsVisible) {
                        $("#gb-scroll-to-top-container-outer").removeClass("sticky").fadeIn();

                        csScrollBtnIsSticky = false;
                        csScrollBtnReset = false;
                        csScrollBtnIsVisible = true;
                    }
                }
            } else {
            	if ($(window).scrollTop() < $("#sw-content-layout-wrapper").offset().top) {
                    if(csScrollBtnIsSticky) {
                        $("#gb-scroll-to-top-container-outer").fadeOut(300, function() {
                            $(this).removeClass("sticky").removeAttr("style");
                        });

                        csScrollBtnIsSticky = false;
                        csScrollBtnReset = false;
                        csScrollBtnIsVisible = false;
                    }
                } else if($(window).scrollTop() >= ($("#sw-content-layout-wrapper").offset().top) && $(window).scrollTop() < (($(".gb.footer.three").offset().top) - $(window).height())) {
                    if(!csScrollBtnIsSticky && csScrollBtnIsVisible || !csScrollBtnIsSticky && !csScrollBtnIsVisible) {
                        $("#gb-scroll-to-top-container-outer").addClass("sticky").fadeIn();

                        csScrollBtnIsSticky = true;
                        csScrollBtnReset = false;
                        csScrollBtnIsVisible = true;
                    }
                } else if($(window).scrollTop() >= (($(".gb.footer.three").offset().top) - $(window).height())) {
                    if(csScrollBtnIsSticky || !csScrollBtnIsVisible) {
                        $("#gb-scroll-to-top-container-outer").removeClass("sticky").fadeIn();

                        csScrollBtnIsSticky = false;
                        csScrollBtnReset = false;
                        csScrollBtnIsVisible = true;
                    }
                }
            }
        }
    }

    function accessibility(){

    	$("#sw-search-input").attr("tabindex","-1");

    	$(".sw-mystart-dropdown").each(function(i) {
            // SET ATTRIBUTES
            $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1)).attr("aria-expanded","false").attr("aria-haspopup","true").attr("role","button");
            $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id"));
            $(".sw-dropdown", this).attr("aria-hidden", "true");
            $(".sw-dropdown-list li", this).attr("role", "menuitem");
        });

        $(".sw-mystart-dropdown").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();
                    $(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");
                    $(".selector", this).attr("aria-expanded", ($(".selector", this).attr("aria-expanded") == "true") ? "false" : "true");

                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                      if(!$(this).hasClass("open")) {
                          // TOGGLE THE DROPDOWN
                          $(this).addClass("open");
                          $(".sw-dropdown-list li:first-child a", this).focus();
                      } else {
                      		$(this).removeClass("open");
                      }
                      $(".sw-dropdown-list li a", this).attr("tabindex","0");
                    // FOCUS FIRST ITEM

                break;
            }
        });

        $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                break;
            }
        });

        $("#cs-sw-myaccount-list li").each(function(){
            $("> a", this).attr("tabindex", "0"); 
        }); 

        $("#sw-myaccount").attr("role","button").attr("aria-expanded","false").attr("aria-haspopup","true");

        $("#sw-myaccount").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();


                    $(this).next("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");


                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                 	if(!$(this).hasClass("open")) {
                          // TOGGLE THE DROPDOWN
                     	$(this).addClass("open").attr("aria-expanded","true");
                      	$(this).next("#cs-sw-myaccount-list").find("li:first-child a").focus();
                    } else {
                      	$(this).removeClass("open clicked-state").attr("aria-expanded","false");
                    }
                    // FOCUS FIRST ITEM

                break;
            }
        });    

        $("#cs-sw-myaccount-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                break;
            }
        });
    }
    function addTranslate(){
    	var pageURL = window.location.href;
		pageURL = encodeURIComponent(pageURL);
    	var translateAddition = '
' +
                                  '
Translate/Traducir' +
                                  '
' +
                                      	'' +
                                            '
' +
                                            '
' +
                                            '
' +
                                            '
' +
                                            '
' +
                                            '
' +
    									'' +
                                        'Additional Languages' +
                                  '' +
                                  '
' +
                              	'';

                                $(".gb-mystart.right").prepend(translateAddition);

        var _this = this;
        // BRANDED TRANSLATOR CLICK
            $("#quick-lang a").click(function(e) {
                e.preventDefault();

                // SET LANGUAGE PAIR COOKIE
                var langPair = $(this).attr("data-lang-code");

                csGlobalJs.CreateCookie({
                    "name": "googtrans",
                    "value": langPair
                });

                // RELOAD THE PAGE
                location.reload();
            }).keydown(function(e) {
                if(e.keyCode == _this.KeyCodes.enter) {
                    e.preventDefault();

                    // SET LANGUAGE PAIR COOKIE
                    var langPair = $(this).attr("data-lang-code");
                    csGlobalJs.CreateCookie({
                        "name": "googtrans",
                        "value": langPair
                    });

                    // RELOAD THE PAGE
                    location.reload();
                }
            });

            $(".sw-mystart-dropdown.translate .selector").click(function(e) {
            	e.preventDefault();
				if($(this).parent().hasClass("open")){
                    $(this).parent().removeClass("open");
                    //$(".sw-dropdown-arrow",this).removeClass("open");
                    $(this),parent().find(".sw-dropdown").slideUp(500, "swing").attr("aria-hidden","true");
                    $(this).attr("aria-expanded","false");
                } else {
                    $(this).parent().addClass("open");
                    //$(".sw-dropdown-arrow",this).addClass("open");
                    $(this).parent().find(".sw-dropdown").slideDown(500, "swing").attr("aria-hidden","false");
                    $(this).attr("aria-expanded","true");
                }
			});

			$(".sw-mystart-dropdown.translate").hover(function() {},
			function() {
				$(".sw-dropdown",this).slideUp(500, "swing").attr("aria-hidden","true");
                $(".selector", this).attr("aria-expanded","false");
			});

            // ADD TRANSLATE
            $(".sw-mystart-dropdown.translate .sw-dropdown .adtnl-languages").creativeTranslate({
                "type": 2, // 1 = FRAMESET, 2 = BRANDED, 3 = API
                "advancedOptions": {
                    "addMethod": "append", // PREPEND OR APPEND THE TRANSLATE ELEMENT
                    "brandedLayout": 1 // 1 = VERTICAL (TEXT UNDER SELECT), 2 = HORIZONTAL (TEXT BESIDE SELECT), 3 = NON-RESPONSIVE MEGA DROPDOWN
                },
                "translateLoaded": function() { }
            });
    }

	function addFramesetTranslate() {
    	//Variables
		var pageURL = window.location.href;
		pageURL = encodeURIComponent(pageURL);
		var translateDropdown = '
' +
						'
Translate/Traducir' +
						'
' +
							'' +
								'
' +
									'' +
								'' +
                                '
' +
									'' +
								'' +
								'
' +
									'' +
								'' +
								'
' +
									'' +
								'' +
								'
' +
									'' +
								'' +
								'
' +
									'' +
								'' +
							'' +
                            'Additional Languages' +
						'' +
						'
' +
					'';
		var pageHost = window.location.host;

		if(pageHost != "translate.googleusercontent.com"){				
