$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=diDtpvud7L%2bjmpSK%2btpq9w%3d%3d&SessionID=91624031-cef9-45c6-bcd3-38dce288b5ca&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f24%2f2020+10%3a59%3a40+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.29.42';
