link.script:
    var scrolledPastNav = false;
    var nav = $("#topnav-fix");
    var breakpoint = $("#topnav_fix_breakpoint");

    var navHeight = 50; //nav.outerHeight();

    function positionNav(){
        var windowTop = $(window).scrollTop();
        var navTop = breakpoint.offset().top;

        if (windowTop >= navTop) {
            nav.addClass('fixed-nav');
            breakpoint.height(navHeight);
            scrolledPastNav = true;
        } else {
            nav.removeClass('fixed-nav');
            breakpoint.height(0);
            scrolledPastNav = false;
        }
    }

    $(window).bind('scroll', function () {
        positionNav();
    });

    $(window).resize(function(){
        positionNav();
    });

