    var lastWidth = $(window).outerWidth();

    function resizeEventSlider() {

        var currWidth = $(window).outerWidth();

        if(lastWidth >= 768 && currWidth < 768 || (lastWidth == currWidth && currWidth < 768)) {
            eventsSlick.slick("unslick");
            eventsSlick.slick({
                prevArrow: 'Previous',
                nextArrow: 'Next'
            });
            lastWidth = currWidth;
        }
        else if (lastWidth < 768 && currWidth >= 768) {
            eventsSlick.slick("unslick");
            eventsSlick.slick({
                dots: false,
                infinite: true,
                rows: 3,
                slidesPerRow: 1,
                slidesToShow: 1,
                prevArrow: 'Previous',
                nextArrow: 'Next'
            });
            lastWidth = currWidth;
        }
    }

    var slides = $(".hp_slideshow_imagebox");
    var schoolbox = $(".hp_slideshow_schoolbox");

    function resizeSlides() {
        if ($(window).outerWidth() > 991.9 && slides.outerHeight() <= schoolbox.outerHeight()) {
            slides.css("min-height", schoolbox.outerHeight());
        }
        else {
            slides.css("min-height", "unset");
        }
    }

    $(window).resize(function () {
        countdown.rebuild();
        resizeSlides();
        resizeEventSlider();
    }).resize();
