    function eventSlider() {
    	$("#sw-content-container1.ui-hp .ui-widget.app.upcomingevents").csArticleSlider({
            "viewerNum": [5, 3, 3, 2, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
            "transitionSpeed": .5, // SECONDS
            "slideFullView": false,
            "extend": function(config, element, ArticleSlider) {}
        });


    }



    function dropdownActions() {
    	var dropdownParent = ".user-options, .translate, .custom-dropdown";
            var dropdownSelector = ".cs-selector";
            var dropdown = ".cs-dropdown";
            var dropdownList = ".cs-dropdown-list"

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
        	});

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case KeyCodes.enter:
                    case KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case KeyCodes.tab:
                    	if($("+ " + dropdown + " " + dropdownList + " a").length) {
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        	$(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case KeyCodes.down:
                    case KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case KeyCodes.left:
                    case KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case KeyCodes.right:
                    case KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
                    case KeyCodes.esc:
                        e.preventDefault();

                        // FOCUS DROPDOWN BUTTON AND CLOSE DROPDOWN
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(dropdownSelector).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    break;
                }
            });

            $(dropdownParent).mouseleave(function(e) {
            	if(e.target.nodeName.toLowerCase() !== "select") {
					$(dropdownList + " a", this).attr("tabindex", "-1");
					$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                }
            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if(!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        }

    function myStart() {

            // NOT LOGGED IN
            //REMOVE PASSKEYS FROM DOM
            $('li#passkeys').remove();

            //BUILD USER OPTIONS MENU
            if($(".sw-mystart-button.signin").length){ //SignIn
				$('.cs-mystart-dropdown.user-options ul').append("
" + $(".sw-mystart-button.signin").html() + "");

                if($(".sw-mystart-button.register").length){ //Register
                    $('.cs-mystart-dropdown.user-options ul').append("
" + $(".sw-mystart-button.register").html() + "");
                }

            }


            $(".user-options .cs-selector").html($(".user-options .cs-selector")
                                           .html()
                                           .replace('User Options', 'Staff Sign In'));



            // ADD TRANSLATE
            $(".cs-mystart-dropdown.translate .cs-dropdown").creativeTranslate({
                "type": 2, // 1 = FRAMESET, 2 = BRANDED, 3 = API
                "advancedOptions": {
                    "addMethod": "append", // PREPEND OR APPEND THE TRANSLATE ELEMENT
                    "brandedLayout": 1 // 1 = VERTICAL (TEXT UNDER SELECT), 2 = HORIZONTAL (TEXT BESIDE SELECT), 3 = NON-RESPONSIVE MEGA DROPDOWN
                },
                "translateLoaded": function() { }
            });


    }

    function checkBreakpoint() {
    	switch(csGetBreakPoint()) {

			case "480": case "320":
					//var text = $('.schools .cs-selector').text();
                    //$('.schools .cs-selector').text(text.replace('Our Schools', ''));

                    $('.rs-menu-btn span').hide();


			break;



            case "desktop": case "768": case "640":
					var text = $('.schools .cs-selector').text();
                    $('.schools .cs-selector').html('Our Schools');


                    $('.rs-menu-btn span').show();
			break;
    	}


    }

    function allyClick(event) {
    	if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
    }

    function toggleSchoolMenu() {
            $('.schools .cs-selector').on('click keydown', function(e) {
                if(allyClick(e)) {
                    if(csGetBreakPoint() == 'desktop') {
                        e.preventDefault();

                        $('#gb-school-list-outer').css('display', 'block');
                        var schoolHeight = $('#gb-school-list-outer').outerHeight();
                        $('#gb-school-list-outer').css('margin-top', -schoolHeight);

                    }else {
                    	$('#gb-school-list-outer').css('display', 'none');
                    	$('#rs-menu-btn').click();
                        $('.schools').click();
                    }

                        if($(this).attr('aria-expanded') == "false") {
                            $(this).attr('aria-expanded', true);
                            $('#gb-school-list-outer').addClass('open').attr('aria-hidden', false);

                            setTimeout(function() {
                                $('#gb-school-tab-list li:eq(0)').find('>a').focus();

                            }, 500);


                        }else {
                            $(this).attr('aria-expanded', false);
                            $('#gb-school-list-outer').removeClass('open').attr('aria-hidden', true);
                            tabInit();


                        } 
                }

            });



    }

    function populateSchoolList() {
    	var elemSchools = "", middleSchools = "", highSchools = "";
            $(".sw-mystart-dropdown.schoollist .sw-dropdown-list > li").each(function() {
                var schoolName = $.trim($("a", this).text()).toLowerCase();


                // CHECK FOR ELEMENTARY
                if(schoolName.indexOf("(es)") > -1) {
                    elemSchools += '
' + $(this).html().replace('(es)', '') + '';
                }

                // CHECK FOR MIDDLE
                else if(schoolName.indexOf("(ms)") > -1) {
                    middleSchools += '
' + $(this).html().replace('(ms)', '') + '';

                }

                // CHECK FOR HIGH
                else if(schoolName.indexOf("(hs)") > -1) {
                    highSchools += '
' + $(this).html().replace('(hs)', '') + '';

                }

                $(".gb-school-panel.elementary").html('' + elemSchools + '');
                $(".gb-school-panel.middle").html('' + middleSchools + '');
                $(".gb-school-panel.high").html('' + highSchools + '');

            });

            tabInit();


    }

    function tabInit() {
    	// INITIALIZATION
        $('.tab-button').removeClass('active');
        $(".tabs-container li:eq(0) .tab-button").addClass("active").attr("aria-selected", "true").attr("tabindex", "0");
        $(".tabs-container li:gt(0) .tab-button").attr("aria-selected", "false").attr("tabindex", "-1");
        $(".gb-school-panel:eq(0)").attr("aria-hidden", "false");
        $('.gb-school-panel:gt(0)').hide();
        $('#gb-school-list-outer').removeClass('open');
        var schoolHeight = $('#gb-school-list-outer').outerHeight();
        	$('#gb-school-list-outer').css('margin-top', -schoolHeight);



    }

    function handleTabEvents() {
       var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

    	$('#gb-school-tab-list a').click(function(e){
            	e.preventDefault();
                $('#gb-school-tab-list a').removeClass('active')
                $(this).addClass('active');
            });

        	$('#gb-school-tab-list a').focus(function(e){
            	var item = $(this).parent().index();
                $('.gb-school-panel').hide().attr("aria-hidden", "true");
                $('.gb-school-panel').eq(item).show().attr("aria-hidden", "false");

            });

            $('#gb-school-tab-list a').keydown(function(e){

                switch(e.keyCode) {

                	// CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                    if($(this).parent().index() == 0) {
                    	$('#gb-school-tab-list a').removeClass('active').attr("aria-selected", "false").attr("tabindex", "-1");
                    	$(this).closest('ul').find("li:last-child > a").focus().addClass('active');

                    }
                    else {
                    $('#gb-school-tab-list a').removeClass('active').attr("aria-selected", "false").attr("tabindex", "-1");
                    	$(this).parent().prev("li").find("a").focus().addClass('active');
                    }

                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        if($(this).parent().is(":last-child")) {
                        	$('#gb-school-tab-list a').removeClass('active').attr("aria-selected", "false").attr("tabindex", "-1");
                        	$(this).closest('ul').find("li:first-child > a").focus().addClass('active');
                        } else {
                        	$('#gb-school-tab-list a').removeClass('active').attr("aria-selected", "false").attr("tabindex", "-1");
                            $(this).parent().next().find("> a").attr("tabindex", "0").focus().addClass('active');
                        }

                    break;

                    // CONSUME ESC KEY
                    case csKeyCodes.esc:
                     e.preventDefault();
                    	$('.school-btn').focus();

                        var schoolHeight = $('#gb-school-list-outer').outerHeight();
                		$('#gb-school-list-outer').css('margin-top', -schoolHeight);

                        $('.schools .cs-selector').attr('aria-expanded', false);
                        $('#gb-school-list-outer').removeClass('open');

                        $('.schools .cs-selector').focus();

                        tabInit();

                    break;


            	}
                	// CONSUME SHIFT + TAB
                    if(e.shiftKey && e.keyCode == 9) {
                        e.preventDefault();
                        if($(this).parent().index() == 0) {
                            $('#gb-school-tab-list a').removeClass('active').attr("aria-selected", "false").attr("tabindex", "-1");
                            $(this).closest('ul').find("li:last-child > a").focus().addClass('active');

                        }else {
                        $('#gb-school-tab-list a').removeClass('active').attr("aria-selected", "false").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("a").focus().addClass('active');
                        }

                    }

            });
    }

    function handlePanelEvents() {
    	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
    	$(".gb-school-panel").each(function() {
    		var link = $("a", this);
            var firstLinkIndex = $(link).index($(link).first());
            var firstLink = $(link).eq($(link).index($(link).first()));
            var lastLinkIndex = $(link).index($(link).last());
            var lastLink = $(link).eq($(link).index($(link).last()));

            $(link).keydown(function(e) {
                var increment;
                var thisIndex = link.index(this);
                var newIndex = -1;

                switch(e.keyCode) {
                    case csKeyCodes.up:
                    case csKeyCodes.left:
                        e.preventDefault();

                        if($(this).is(firstLink)) {
                            newIndex = lastLinkIndex;
                        } else {
                            newIndex = thisIndex - 1;
                        }
                    break;

                    case csKeyCodes.down:
                    case csKeyCodes.right:
                        e.preventDefault();

                        if($(this).is(lastLink)) {
                            newIndex = firstLinkIndex;
                        } else {
                            newIndex = thisIndex + 1;
                        }
                    break;

                    case csKeyCodes.esc:
                        e.preventDefault();

                        $('.schools .cs-selector').attr('aria-expanded', false).focus();
                        $('#gb-school-list-outer').removeClass('open');
                        tabInit();
                    break;

                    case csKeyCodes.tab:
                        e.preventDefault();

                        if(e.shiftKey) {
                        	$("#gb-school-tab-list .tab-button.active").focus();
                        } else {
                        	$('.schools .cs-selector').attr('aria-expanded', false).focus();
                            $('#gb-school-list-outer').removeClass('open');
                            tabInit();
                        }
                    break;
                }

                if(newIndex != -1) {
                    link.eq(newIndex).focus();
                }
            });
        });
    	/*$('#gb-school-panel-list a').keydown(function(e){
            	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

            	switch(e.keyCode) {

                	// CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
