	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
                checkBreakpoint();
                $('.custom-dropdown > div').addClass('cs-selector');
                $('.mmg-description-wrap').prependTo('#cs-fullscreen-video-outer');

                $('.schools .cs-selector').attr('aria-expanded', false);
                $('#gb-school-list-outer').removeClass('open').attr('aria-hidden', true);;
                tabInit();
            	$("#gb-header").css("padding-left", $("#gb-school-banner").outerWidth(true));
			break;
			case "768":
				//csVideoResize();
                checkBreakpoint();
                $('.custom-dropdown > div').removeClass('cs-selector');
                $('.mmg-description-wrap').prependTo('#hp-content');
                $('#gb-school-list-outer').css('display', 'none').attr('aria-hidden', true);

                $("#gb-header").css("padding-left", "");
			break;
			case "640":
				//mobileSearch();
                checkBreakpoint();
				//csVideoResize();
                $('.custom-dropdown > div').removeClass('cs-selector');
                $('.mmg-description-wrap').prependTo('#hp-content');
                $('#gb-school-list-outer').css('display', 'none').attr('aria-hidden', true);


             	$("#gb-header").css("padding-left", "");
			break;
			case "480":
				//mobileSearch();
				popularItemsSize();
				//csVideoResize();
                checkBreakpoint();
				//mobileGlobalIcons();
                $('.custom-dropdown > div').removeClass('cs-selector');
                $('.mmg-description-wrap').prependTo('#hp-content');
                $('#gb-school-list-outer').css('display', 'none').attr('aria-hidden', true);


                $("#gb-header").css("padding-left", "");
			break;
			case "320":
				//mobileSearch();
				popularItemsSize();
				//csVideoResize();
                checkBreakpoint();
				//mobileGlobalIcons();
                $('.custom-dropdown > div').removeClass('cs-selector');
                $('.mmg-description-wrap').prependTo('#hp-content');
                $('#gb-school-list-outer').css('display', 'none').attr('aria-hidden', true);

                $("#gb-header").css("padding-left", "");
			break;
		}
	}
