/* ------------------------------   STICKY HEADER  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */
	function stickyHeader(){
		$(document).scroll(function() {
			var distanceFromTop = $(this).scrollTop();

            if(distanceFromTop >= 146){
                $("#gb-page").addClass("sticky-header");
                if($('#gb-page[data-hc-toggle="true"]').length){
            		$("#cs-contrast-bar-outer").addClass("sticky-header");
            	}
            } else {
                $("#gb-page").removeClass("sticky-header");
                if($('#gb-page[data-hc-toggle="true"]').length){
            		$("#cs-contrast-bar-outer").removeClass("sticky-header");
            	}
            }

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                }
            }

            $("#sw-maincontent").css({
                "display": "block",
                "position": "relative",
                "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
            });
		});
	}

