/* ------------------------------   POPULAR ITEMS  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */
	function popularItems(){

		$("#popular-items-button").on('click keypress', function(event){
			if(globalClick(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
				event.preventDefault();

				if($(this).hasClass("open")){
					$("#popular-items").slideUp(300, "swing");
					$(this).attr("aria-expanded","false");
					$("#popular-items").attr("aria-hidden","true");
					$(this).removeClass("open");
				} else {
					$("#popular-items").slideDown(300, "swing");
					$(this).attr("aria-expanded","true");
					$("#popular-items").attr("aria-hidden","false");
					$(this).addClass("open");
				}				
			}
		});

		$(".pi-header").on('click keypress', function(event){
			if(globalClick(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
				event.preventDefault();

				if($(this).parent().hasClass("open")){
					$(this).next().slideUp(300, "swing");
					$(this).parent().removeClass("open");
					$(this).next().attr("aria-hidden","true");
					$(this).attr("aria-expanded","false");
				} else {
                	//CLOSE ANY OPEN LISTS
                    $("#popular-items li.open ul").slideUp(300, "swing");
                    $("#popular-items li.open ul").attr("aria-hidden","true");
                    $("#popular-items li.open h2").attr("aria-expanded","false");
                    $("#popular-items li.open").removeClass("open");

					$(this).next().slideDown(300, "swing");
					$(this).next().attr("aria-hidden","false");
					$(this).attr("aria-expanded","true");
					$(this).parent().addClass("open");
				}
			}
		});
	}

	function popularItemsSize(){
		var popularItemsHeight = $(window).height() - 96;
		$("#popular-items").height(popularItemsHeight);
	}

