	function schoolList(){
    	head.ready("schoolMinibase", function(){

			$.schoolListMinibase({

	            'miniBaseDomainID': 4, 
	            'miniBaseModuleInstanceID': 7115, 
	            'cache': true,
	            'schoolListReady': function(schoolList) {


                    var elemSchools = "", middleSchools = "", highSchools = "";
                    $.each(schoolList, function(index, school) {
                        //var schoolName = $.trim($("a", this).text()).toLowerCase();
						var schoolType = school.schoolType.toLowerCase();

                        // CHECK FOR ELEMENTARY
                        if(schoolType.indexOf("elementary") > -1) {
                            elemSchools += '
' + school.schoolName + '';
                        }

                        // CHECK FOR MIDDLE
                        else if(schoolType.indexOf("middle") > -1) {
                            middleSchools += '
' + school.schoolName + '';

                        }

                        // CHECK FOR HIGH
                        else if(schoolType.indexOf("high") > -1) {
                            highSchools += '
' + school.schoolName + '';

                        }

                        $(".gb-school-panel.elementary").html('' + elemSchools + '');
                        $(".gb-school-panel.middle").html('' + middleSchools + '');
                        $(".gb-school-panel.high").html('' + highSchools + '');

                        buildColumns({
                            "columns": [4, 4, 4, 4, 4],
                            "selector": ".gb-school-panel"
                        });

                    });

                    tabInit();


	                //The returned value is an array of objects and each object is a school record.
	                //The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible


	                //CREATE AN EMPTY ARRAY FOR SCHOOL CATEGORIES
                    schoolCategories = [];

                    //LOOP THROUGH EACH SCHOOL TO COLLECT CATEGORY NAMES
                    for(var i in schoolList) {
                    	var schoolType = schoolList[i].schoolType.toLowerCase().split(" ");

                        //IF THE CATEGORY DOES NOT ALREADY EXIST IN THE ARRAY, PUSH IT INTO THE ARRAY
                        schoolCategories.indexOf(schoolType[1]) === -1 ? schoolCategories.push(schoolType[1]) : "";  
                    }

                    //BUILD THE SCHOOL LIST CONTAINERS BASED ON THE DETERMINED CATEGORIES
                    schoolCategoriesHTML = '';

           			//LOOP THROUGH EACH CATEGORY AND CREATE THE HTML FOR IT
                    for(var i in schoolCategories) {
                    	schoolCategoriesHTML += '
'+
                            							'
'+schoolCategories[i]+' Schools'+
                                                        ''+
                                                    '';
                    }

                    //INSERT THE SCHOOL LIST CONTAINERS INTO THE PAGE
                    $("#school-list-container .gb-section").append(schoolCategoriesHTML);

					//LOOP THROUGH EACH SCHOOL, AND PLACE IT IN THE PROPER LIST
                    var mobileSchools = '
ELEMENTARY SCHOOLS';
	                for(var i in schoolList) {
                    	var schoolCat = schoolList[i].schoolType.toLowerCase().split(" ");

	                    if(schoolList[i].schoolVisible == 'true' || schoolList[i].schoolVisible == 'TRUE') {
							$("#"+schoolCat[1]+"-schools").append('
' + schoolList[i].schoolName + ''); 


                            //ALSO PLACE THE SCHOOLS IN THE POPULAR ITEMS MOBILE MENU

                            //COUNT TO SEE WHEN TO PUT IN THE MIDDLE AND HIGH SCHOOL HEADERS
                            if(i == 23){
                            	mobileSchools += '
MIDDLE SCHOOLS
' + schoolList[i].schoolName + '';
                            } else if(i == 37){
                            	mobileSchools += '
HIGH SCHOOLS
' + schoolList[i].schoolName + '';
                            } else {
                            	mobileSchools += '
' + schoolList[i].schoolName + '';
                            }

	                    }
                    }
                    $("#pi-schools-list").append(mobileSchools);
                    handleTabEvents();
                    handlePanelEvents();
	            } 
	        });    
		});

		$(".ac-dropdown-container.school-list .selector").on('click keypress', function(event){
	        if(globalClick(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
	            event.preventDefault();

	            if($(this).parent().hasClass("open")){
	            	closeSchoolList();
	            } else {
	            	openSchoolList();
	            }
			}
	    });

	    $("#school-list-container").hover(function(e) {
			e.preventDefault();
		}, function() {
        	closeSchoolList(); 	
		});
	}

    function buildColumns(props) {
    	var columnNums = props.columns;
        var selector = props.selector;

        // RETURN BREAKPOINT INDEX
        var bp = function() {
            switch(csGetBreakPoint()) {
                case "desktop": return 0; break;
                case "768": return 1; break;
                case "640": return 2; break;
                case "480": return 3; break;
                case "320": return 4; break;
            }
        }

        // SET COLUMN NUM AND OTHER VARS
        var columnNum = columnNums[bp()];
        var endRange;

        $(selector).each(function() {
            if($(".holder > li", this).length > 0){
                // RETURN THE LI'S TO THE ORIGINAL UL
                $(".holder", this).append($(".school-column > li", this));

                // REMOVE COLUMN CONTAINER FOR REBUILD
                $(".school-columns", this).remove();

                // GET SHORTCUT NUM
                var schoolNum = $(".holder > li", this).length;

                // ADD COLUMN CONTAINER
                $(this).prepend('
');

                // LOOP TO BUILD COLUMNS
                for(var i = 0; i < columnNum; i++) {
                    // KEEP FROM ADDING EMPTY UL'S TO THE DOM
                    if(i < schoolNum) {
                        // IF schoolNum / columnNum REMAINDER IS BETWEEN .0 AND .5 AND THIS IS THE FIRST LOOP ITERATION
                        // WE'LL ADD 1 TO THE END RANGE SO THAT THE EXTRA LINK GOES INTO THE FIRST COLUMN
                        if((schoolNum / columnNum) % 1 > 0.0 && (schoolNum / columnNum) % 1 < 0.5 && i == 0) {
                            endRange = Math.round(schoolNum / columnNum) + 1;
                        } else if((schoolNum / columnNum) % 1 == 0.5 && i >= (columnNum / i)) {
                            endRange = Math.round(schoolNum / columnNum) - 1;
                        } else {
                            endRange = Math.round(schoolNum / columnNum)
                        }

                        // ADD THE COLUMN UL
                        $(".school-columns", this).append('');

                        // MOVE THE RANGE OF LI'S TO THE COLUMN UL
                        for(var r = 1; r <= endRange; r++) {
                            if($(".holder > li:first-child", this).length) {
                                $(".school-column.column-" + (i + 1), this).append($(".holder > li:first-child", this));
                            }
                        }
                    }
                }

                // HIDE THE ORIGINAL UL
                $(".holder", this).hide();
            }
        });
    }

	function closeSchoolList(){
		$("#school-list-container").attr("aria-hidden", "true").slideUp(300, "swing");
		$(".ac-dropdown-container.school-list .selector").attr("aria-expanded","false"); 
		$(".ac-dropdown-container.school-list").removeClass("open"); 
		$(".ac-dropdown-container.school-list .selector").focus();
	}

	function openSchoolList(){
		$("#school-list-container").slideDown(300, "swing", function(){
			setTimeout(function(){
				$(".schools-listing:first > li:first a").focus();
			}, 100);
		});

		$(".ac-dropdown-container.school-list .selector").attr("aria-expanded","true");
		$("#school-list-container").attr("aria-hidden","false");
		$(".ac-dropdown-container.school-list").addClass("open");
	}

	function controlDropdowns(event){
		var dropdownToOpen = $(event.currentTarget).parent().find(".ac-dropdown-list").attr("id");
		if($("#"+dropdownToOpen).parent().parent().hasClass("open")){
			$("#"+dropdownToOpen).parent().attr("aria-hidden", "true").slideUp(300, "swing"); 
			$("#"+dropdownToOpen).parent().parent().removeClass("open");
			$("#"+dropdownToOpen).parent().prev().attr("aria-expanded","false");
		} else {
			$("#"+dropdownToOpen).parent().attr("aria-hidden","false").slideDown(300, "swing", function(){
				setTimeout(function(){
					$("#"+dropdownToOpen).find("li:first a").focus();
				}, 100);
			});
			$("#"+dropdownToOpen).parent().parent().addClass("open");
			$("#"+dropdownToOpen).parent().prev().attr("aria-expanded","true");
		}
	}
