	function dynamicContent() {

		// ********** MYSTART ********** //
		$(".ac-dropdown-container:not(.ac-dropdown-container.school-list) .selector").on('click keypress', function(event){
	        if(globalClick(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
	            event.preventDefault();
	            controlDropdowns(event);
	        }
	    });

		//GLOBALLY CLOSE DROPDOWNS WHEN HOVERED AWAY FROM
	    $(".ac-dropdown-container:not(.ac-dropdown-container.school-list)").hover(function(e) {
			e.preventDefault();
		}, function() {

			$(".ac-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
			$(this).removeClass("open"); 
		});

		//SHOW USER OPTIONS IF THEY ARE LOGGED IN
		if($("#sw-mystart-account").length){
        	$(".ac-dropdown-container.user-options").show();
        	$(".ac-mystart-button.manage").show();
