        	 //HIDE REGISTER BUTTON IF HIDDEN IN SITE MANAGER
              if(!$("#ui-btn-register").length){
                  $(".ac-mystart-button.signin").addClass("no-register").show();
              } else {
              	$(".ac-mystart-button.user").not(".ac-mystart-button.manage").show();
              }

        }

		// ********** HEADER ********** //

		 // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/NC02214550/Centricity/Template/GlobalAssets/images///logos/logo.png");
            if(showLogo) {
                if((logoSrc != "") && logoSrc.indexOf("default-man") == -1) {
                    $("#gb-logo, #gb-channel-list-sticky-branding").append('');
                } else {
                    $("#gb-logo, #gb-channel-list-sticky-branding").append('');
                }
            }



            // ADD SITENAME
            var siteNameOne = $.trim("Johnston County");
            var siteNameTwo = $.trim("Public Schools");
            var siteNameBegin = '';
            var siteNameEnd = '';



            if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
                $(".footer .gb-sitename").prepend("
" + siteNameOne + "");


            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
                $(".footer .gb-sitename").prepend("
" + siteNameTwo + "");

            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");
                $(".footer .gb-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");


            }





            if(!(showLogo)) {
            	$("#gb-logo").remove();
            }






		// ********** CHANNEL BAR ********** //
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

		// ********** BODY ********** //

		//FIX FIRST BREADCRUMB
        $(".ui-breadcrumb-first span").text("Home").show();

		// AUTO FOCUS SIGN IN FIELD
		$("#swsignin-txt-username").focus();

		//PLACE THE HEADLINES READ MORE BUTTON INTO THE APP HEADER, AND ADJUST RSS BUTTON
		$(".headlines").each(function(){
			if($(this).find("a.more-link").length){
				var moreLinkURL = $(this).find("a.more-link").attr("href");
				var rssLinkURL = $(this).find(".app-level-social-rss > a").attr("href");
				$(this).find(".ui-widget-header").append("
