	function modEvents(){
		head.ready("upcomingEvents", function(){
			if (!$(".upcomingevents .joel-month").length) {
		        $(".upcomingevents").modEvents({
		            columns     : "no",
		            monthLong   : "yes",
		            dayWeek     : "yes"
		        });
		        eventsByDay(".upcomingevents .ui-articles");
		    }
		});
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $("li", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	        	var prevDate = $(this).prev().find("h1.ui-article-title.sw-calendar-block-date").html();

	          $('.ui-article', this).addClass('upcoming-column-container ui-clear').prepend('

' + prevDate + '');
	        }

	        //NEED A SPACE BETWEEN MONTH AND DATE
	        $(this).find(".joel-month").append(" ");
	    });
	}


	function navHPEvents(){
		resetAriaAttributes();

		//SET WIDTH OF HOMEPAGE EVENTS UL
		var numEvents = $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles li").length;
		var eventsContainerWidth = numEvents * 260;
		$("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles").css("width",eventsContainerWidth);

		//APPEND BUTTONS TO THE APP HEADER
		if(!$("#hp-content-top div.ui-widget.app.upcomingevents .ui-widget-header .event-control").length){
			$("").insertAfter("#hp-content-top div.ui-widget.app.upcomingevents .ui-widget-header h1");
		}


		//CHECK FOR SCREEN WIDTH. THIS WILL DETERMINE HOW MANY EVENTS SHOW AND HOW MUCH THE SLIDER MOVES WHEN ADVANCED
		var screenWidth = $(window).width();

		if(screenWidth <= 767){
			var leftMostEvent = 0;
			var rightMostEvent = 2;
		} else {
			var leftMostEvent = 0;
			var rightMostEvent = 4;
		}

		setInitiallyVisibleAriaAttributes();

        if(!$("#hp-content-top").hasClass('initial')){
        	$(".event-control").on('click', function(event){
                if(globalClickForButtons(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();

                    //if(screenWidth <= 767){

                        //NEXT
                        if($(this).hasClass("next")){
                            if(rightMostEvent != numEvents-1){
                                $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles").animate({
                                    //left: "-=209"
                                    left: "-=" + $(".ui-articles > li:first").outerWidth(true)
                                }, 1000, function() {
                                    resetAriaAttributes();
                                    leftMostEvent++;
                                    rightMostEvent++;
                                    setAriaAttributesOnVisible();
                                });
                            } else {
                                $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles").animate({
                                    left: "0"
                                }, 1000, function() {
                                    resetAriaAttributes();
                                    leftMostEvent = 0;
                                    rightMostEvent = 2;
                                    setInitiallyVisibleAriaAttributes();
                                });
                            }
                        } 

                        //PREV
                        if($(this).hasClass("prev")){
                            if(leftMostEvent != 0){
                                $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles").animate({
                                    //left: "+=209"
                                    left: "+=" + $(".ui-articles > li:first").outerWidth(true)
                                }, 1000, function() {
                                    resetAriaAttributes();
                                    leftMostEvent--;
                                    rightMostEvent--;
                                    setAriaAttributesOnVisible();
                                });
                            }
                        }
                    /*} else {

                        //NEXT
                        if($(this).hasClass("next")){
                            if(rightMostEvent != numEvents-1){
                                $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles").animate({
                                    //left: "-=260"
                                    left: "-=" + $(".ui-articles > li:first").outerWidth(true)
                                }, 1000, function() {
                                    resetAriaAttributes();
                                    leftMostEvent++;
                                    rightMostEvent++;
                                    setAriaAttributesOnVisible();

                                });
                            } else {
                                $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles").animate({
                                    left: "0"
                                }, 1000, function() {
                                    resetAriaAttributes();
                                    leftMostEvent = 0;
                                    rightMostEvent = 4;
                                    setAriaAttributesOnVisible();
                                });
                            }
                        } 

                        //PREV
                        if($(this).hasClass("prev")){
                            if(leftMostEvent != 0){
                                $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles").animate({
                                    //left: "+=260"
                                    left: "+=" + $(".ui-articles > li:first").outerWidth(true)
                                }, 1000, function() {
                                    resetAriaAttributes();
                                    leftMostEvent--;
                                    rightMostEvent--;
                                    setAriaAttributesOnVisible();
                                });
                            }
                        }
                    }*/ // END SCREEN WIDTH CHECK
                } //END CLICK CHECK
            }); //END CLICK

            // BACK SWIPE
            $(document).on("swiperight", "#hp-content-top div.ui-widget.app.upcomingevents .ui-widget-detail", function(e) {
                if(leftMostEvent != 0){
                    $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles").animate({
                        //left: "+=209"
                        left: "+=" + $(".ui-articles > li:first").outerWidth(true)
                    }, 1000, function() {
                        resetAriaAttributes();
                        leftMostEvent--;
                        rightMostEvent--;
                        setAriaAttributesOnVisible();
                    });
                }
            });

            // NEXT SWIPE
            $(document).on("swipeleft", "#hp-content-top div.ui-widget.app.upcomingevents .ui-widget-detail", function(e) {
                if(rightMostEvent != numEvents-1){
                    $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles").animate({
                        //left: "-=209"
                        left: "-=" + $(".ui-articles > li:first").outerWidth(true)
                    }, 1000, function() {
                        resetAriaAttributes();
                        leftMostEvent++;
                        rightMostEvent++;
                        setAriaAttributesOnVisible();
                    });
                } else {
                    $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles").animate({
                        left: "0"
                    }, 1000, function() {
                        resetAriaAttributes();
                        leftMostEvent = 0;
                        rightMostEvent = 2;
                        setInitiallyVisibleAriaAttributes();
                    });
                }
            });
        }
		$("#hp-content-top").addClass("initial");


		function resetAriaAttributes(){
			$("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles li").each(function(){
				$(this).attr("aria-hidden","true");
				$(this).find(".sw-calendar-block-title a").attr("tabindex","-1");
			});
		}

		function setAriaAttributesOnVisible(){
			for (i = leftMostEvent; i <= rightMostEvent; i++) { 
			    $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles li").eq(i).attr("aria-hidden","false");
			    $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles li").eq(i).find(".sw-calendar-block-title a").attr("tabindex","0");
			}
			if(leftMostEvent == 0){
				$(".event-control.prev").attr("tabindex","-1");
			} else {
				$(".event-control.prev").attr("tabindex","0");
			}
		}

		function setInitiallyVisibleAriaAttributes(){
			for (i = 0; i <= rightMostEvent; i++) { 
			    $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles li").eq(i).attr("aria-hidden","false");
			    $("#hp-content-top div.ui-widget.app.upcomingevents .ui-articles li").eq(i).find(".sw-calendar-block-title a").attr("tabindex","0");
			}
		}


	}	
