	function useChannelForSection() {
		// USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
		if(!$("div.sp.column.one .ui-widget-header h1").length) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    	}

	function searchText() {
		//SEARCH TEXT
            $("#gb-search-input").focus(function() {
            	setTimeout(function() {
                  if($("#gb-search-input").val() == "Search Our Site") {
                      $("#gb-search-input").val("");
                  }
                }, 500);
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search Our Site");
                }
            });

             $('#search').on('click keydown', function(e) {
            	if(allyClick(e)) {
                  		e.preventDefault();

                        if($(this).attr('aria-expanded') == "false") {
                        	$(this).attr('aria-expanded',true).attr('aria-label', 'Close Search Form');
                            $('#gb-search-input').addClass('open').attr('tabindex', 0).attr('aria-hidden', false);
                            $("#gb-search-input").val("");
                            $('#gb-search-form').attr('aria-hidden', false);


                            setTimeout(function() {
                            	$('#gb-search-input').focus();
                            }, 500);

            			}else {
                        	$(this).attr('aria-expanded',false).attr('aria-label', 'Open Search Form');;
                            $('#gb-search-input').removeClass('open').attr('tabindex', -1).attr('aria-hidden', true);;
                            $("#gb-search-input").val("Search Our Site");
                            $('#gb-search-form').attr('aria-hidden', true);
                            setTimeout(function() {
                            	$('#search').focus();
                            }, 500);
                        }
                    }

                    $(document).on("keyup", function() {
                    	if($("#gb-search-form").attr("aria-hidden") == "false" && !$("#gb-search-form").find(":focus").length) {
                        	$('#search').attr('aria-expanded',false).attr('aria-label', 'Open Search Form');;
                            $('#gb-search-input').removeClass('open').attr('tabindex', -1);
                            $("#gb-search-input").val("Search Our Site");
                            $('#gb-search-form').attr('aria-hidden', true);
                        }
                    });

                    $('#sw-search-input').off();
                    $('#gb-search-input').on('keydown', function(e){
                        if(e.which == 13){//Enter key pressed
                            e.preventDefault();
                             if($.trim($("#gb-search-input").val()) != "Search Our Site" && $.trim($("#gb-search-input").val()) != "") {


                                window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                            }

                        } 


                    });


            });
	}

	function calendarListView() {
		// AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		switch(csGetBreakPoint()) {
			case "desktop": case "768": case "640":
				$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
			break;
			case "480": case "320":
				$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
				$(".ui-btn-toggle.month").removeClass("active");
				$(".ui-btn-toggle.list").addClass("active");
			break;
		}
	}

	function appAccordion() {
    	head.ready("appAccordion", function(){

        });
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}
