	function accessibilityFunctions(){
        $("#sw-search-button").attr({
        	"aria-label":"Click to search",
        	"role":"button"
        });

		$("#sw-mystart-mypasskey").attr({
			"aria-label":	"Open My Passkey",
			"role":		"button"
		});

        $("footer[google-map-data='false'] #manual-google-address").remove();

		// KEY MAPPING
		var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40, "tab": 9 };

		//SEARCH BUTTON
		$("#sw-search-button").keydown(function(e){
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.enter:
				case csKeyCodes.space:
					e.preventDefault();

					$(this).click();

				break;
			}

		});

		//GLOBAL KEYBOARD NAV FOR DD SELECTORS
		$(".ac-dropdown-container .selector:not(.ac-dropdown-container.school-list)").keydown(function(e) {
			var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
            var parentContainer = parentContainer.split(" ");
            var parentContainer = parentContainer[1];

			// CAPTURE KEY CODE
			switch(e.keyCode) {

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						$(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                        $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
					} else {
						e.preventDefault();
						$(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                        $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");

						switch(parentContainer){
							case "user-options":
								$("#gb-logo a").focus();
							break;
							case "translate":
								$(".ac-dropdown-container.popular-links .selector").focus();
							break;
							case "popular-links":
								$("#sw-search-input").focus();
							break;
						}

					}
				break;

				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();

				break;
			}
		});

		//GLOBAL KEYBOARD NAV FOR DD LINKS
		$(".ac-dropdown-container li a").keydown(function(e) {
            var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
            var parentContainer = parentContainer.split(" ");
            var parentContainer = parentContainer[1];
            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".ac-dropdown-container."+parentContainer+" .selector").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						e.preventDefault();
                        switch(parentContainer){
                                case "translate":
                                  $(".ac-dropdown-container.school-list .selector").focus();
                                  $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing");
                                  $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                  $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                break;

                                case "popular-links":
                                  $(".ac-dropdown-container.translate .selector").focus();
                                  $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing");
                                  $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                  $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                break;
                         }
					} else {
						e.preventDefault();
                        switch(parentContainer){
                                case "translate":
                                  $(".ac-dropdown-container.popular-links .selector").focus();
                                  $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing");
                                  $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                  $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                break;

                                case "popular-links":
                                  $("#sw-search-input").focus();
                                  $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing");
                                  $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                  $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                break;
                         }


					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:last-child > a").focus();
				break;

				case csKeyCodes.esc:
					$(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                        $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
						$(".ac-dropdown-container."+parentContainer+" .selector").focus();
				break;
			}
		});

        /***** || BEGIN SCHOOL LIST || *****/
		$(".ac-dropdown-container.school-list .selector").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						closeSchoolList();
					} else {
						e.preventDefault();
						closeSchoolList();
						$(".ac-dropdown-container.translate .selector").focus();
					}
				break;

				// CONSUME DOWN AND RIGHT ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".schools-listing:first > li:first a").focus();

				break;
			}
		});

		$("li.school a").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						//CHECK TO SEE IF IS IN FIRST LIST
						if($(this).parent().parent().parent().is(":first-child")){
							$(".ac-dropdown-container.school-list .selector").focus();
						} else {
							$(this).parent().parent().parent().prev().find(".schools-listing li:last a").focus();
						}
						// FOCUS DROPDOWN BUTTON

					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						//CHECK TO SEE IF IS IN LAST LIST
						if($(this).parent().parent().parent().is(":last-child")){
							$(".schools-listing:first > li:first a").focus();
						} else {
							// FOCUS FIRST ITEM IN NEXT LIST
							$(this).parent().parent().parent().next().find(".schools-listing li:first a").focus();
						}
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						e.preventDefault();

                        closeSchoolList();
						$("#sitemap-link").focus();
					} else {
						e.preventDefault();

						closeSchoolList();
						$(".ac-dropdown-container.translate .selector").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".schools-listing:first > li:first a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".schools-listing:last > li:last a").focus();
				break;

				case csKeyCodes.esc:
					closeSchoolList();
				break;
			}
		});
		/***** || END SCHOOL LIST || *****/

	}
