$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=kooHPAGM0hRwe2FHl2J9Cw%3d%3d&SessionID=924d16e3-3540-478e-9693-91d4dd5a56ae&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f24%2f2020+11%3a02%3a34+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.77.231';
