    jQuery(document).ready(function(){
    var jq=jQuery;
    jq("a[rel^='prettyPhoto']").prettyPhoto({theme:"light_rounded"}); //incase you want to use pretty photo for other images too, otherwise pass anything as selecter, we need this call to make PrettyPhoto available for later call
    /*get the gallery Id from the dom element eg. 
 gives 32'*/
    function pg_get_id(el){
        var eid=el.id;
        var beg=eid.lastIndexOf('_');//dom id is somename_something_idofgallery/media
        var id=eid.slice(beg+1);
        return id;
    }
    /* Bind click event*/
  
    jq(".bp-pretty-gallery a,.sitewide-galleries-sitewide a,.bp-gallery-embeded-galleries a").live("click",function(){

//find parent gallery id
    var gallery=jq(this).parent().get(0);
    var gallery_id=pg_get_id( gallery);
    if(!gallery_id||!jq(gallery).hasClass("gallery-type-photo"))
        return;//for those where we don't have the id
       fetch_gallery(gallery_id);

return false;
});


