function fetch_gallery(gallery_id){
 //fetch all the medias in the gallery using ajax
jq.post( ajaxurl, {
                action: 'fetch_gallery_media',
                'cookie': encodeURIComponent(document.cookie),
                'gallery_id':gallery_id
                },
        function(response){
                    var media=JSON.parse(response);//media is a object havinf 3 array elemnts media.titles, array of titles, media.description, array of descriptions, media.medias, array of media srcs
                    if(media.error!=undefined)
                        return true;//
                    jQuery.prettyPhoto.open(media.images,media.titles,media.description); //call Your Viwere here as we have arra of Image srcs, titles,desc
        
                    });   
}

