if ((/Android|iPhone|iPad|iPod|BlackBerry|Windows Phone/i).test(navigator.userAgent || navigator.vendor || window.opera)) {
	$('body').removeClass('desktop');
}
$(document).ready(function() {

	$(window).on('scroll', function() {
		var delay = 0;
		$('.main .col-item').children().each(function(i) {

			var bottom_of_object = $(this).offset().top;
			var bottom_of_window = $(window).scrollTop() + $(window).height();
			if (bottom_of_window > bottom_of_object) {

				var $li = $(this);
				setTimeout(function() {
					$li.addClass('active');
				}, delay += 30);
			}
		});
	});
	$(window).trigger('scroll');


	$('.slider-container').bxSlider({
		mode: 'fade',
		auto: true,
		autoHover: true,
		adaptiveHeight: true,
		onSlideAfter: function() {
			$('.bx-pager-link').attr('tabindex', '-1').attr('aria-hidden', 'true');
		}

	});
		$('.news-slider').bxSlider({
			auto: true,
			autoHover: true,
			slideMargin: 10,
			adaptiveHeight: true,
			controls: false,
			onSliderLoad: function (currIndex) {
				var newsheight = $(".news:nth-child(" + (currIndex + 2) + ") .right-content").height() + 20;
				$(".main .col-item.news-wrapper .bx-wrapper .bx-pager").css({ bottom: newsheight});

				var maxHeight = -1;

				$('.news').each(function() {
					maxHeight = maxHeight > $(this).height() ? maxHeight : $(this).height();
				});
				$(".main .col-item.news-wrapper .slide-height").css({height: maxHeight});
			},
			onSlideBefore: function(currentSlideNumber, totalSlideQty, currentSlideHtmlObject) {
				var newsheight = $(".news .right-content").eq(currentSlideHtmlObject + 1).height() + 20;
				$(".main .col-item.news-wrapper .bx-wrapper .bx-pager").css({ bottom: newsheight});
			}
		});
		$('a.bx-prev, a.bx-next, .bx-clone a, .bx-pager-link').attr('tabindex', '-1').attr('aria-hidden', 'true');

});
