body.script:
window.onload = init;
var topnavButton = document.getElementById('sitenav_main_mobile_toggle');
var topnavDisplay = document.getElementById('sitenav_main');

var school_topnavButton = document.getElementById('sitenav_school_mobile_toggle');
var school_topnavDisplay = document.getElementById('school_sitenav_main');

function init() {
	topnavButton.onclick = toggleNav;
	school_topnavButton.onclick = toggleNavSchool;
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
}
function toggleNavSchool(){
	school_topnavDisplay.classList.toggle("open");
}
