    var speed = 350;

    function numbinator() {
        var num = $('.counter1').html();
        var hasPercent = '';
        var hasBucks = '';

        if (num.includes('%')) {
            hasPercent = '%';
        };
        if (num.includes('$')) {
            hasBucks = '$';
        };
        num = num.replace(/[^0-9\.]/g, '');
        var zero = 0;
        var timmy = setInterval(function () {
            if (num.includes('.')) {
                $('.counter1').html(hasBucks + (Math.round(zero * 10) / 10).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",") + hasPercent);
            }
            else {
                $('.counter1').html(hasBucks + Math.round(zero).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",") + hasPercent);
            }
            if (zero < num) {
                zero += num / speed;
            }
            else {
                $('.counter1').html(hasBucks + num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",") + hasPercent);
                clearInterval(timmy);
            }
        }, 1);

