        window.resizeMiniCalendar20200424111957556 = function () {
        var calId = 'miniCal20200424111957556';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200424111957556';
        var calMonthFullId = 'miniCalMonthFull20200424111957556';
        var calMonthShortId = 'miniCalMonthShort20200424111957556';
        var calGoToCalFullId = 'miniCalGotToCalFull20200424111957556';
        var calGoToCalShortId = 'miniCalGoToCalShort20200424111957556';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

