	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			var imgWidth = $(window).width();
			var initW = 1500, initH = 977;
			var ratio = initH / initW;
			imgHeight = imgWidth * ratio;

			$("#gb-background-photo img").css("width", imgWidth);
			$("#gb-background-photo img").css("height", imgHeight);

			var contentWidth = $("#hp-content-upper").width();
			var goToTopOffset = ((getWidth - contentWidth)/2 - 56)/2;
			$("#gb-go-to-top").css("right", goToTopOffset);


		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView();

		} if (getWidth >= 320 && getWidth < 480) { // 320+
			appView();
		}
	}

	function photoGallery(){
