	function eventsByDay(container) {
		//DOC move all events with the same dates into the same container
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});

		if($(".hp").length) {

			// ADD CLASSES TO ARTICLES
			$("#hp-content-lower .upcomingevents .ui-articles").wrap("
");
			$("#hp-content-lower .upcomingevents li").first().addClass("first");
			$("#hp-content-lower .upcomingevents li").last().addClass("last");
			var eventClass = ["one", "two", "three", "four", "five"];
			$("#hp-content-lower .upcomingevents li").each(function() {
				var thisIndex = $(this).index();
				$(this).addClass("event-" + eventClass[thisIndex]);
			});
			$('#hp-content-lower .ui-widget.app.upcomingevents .ui-widget-detail .ui-articles').each(function(){
				$(this).addClass("ui-clear");
			});
		}
	}

