			$("a.ui-btn-toggle.list").click();
		}
	}

	function socialIcons(){
		// SET UP THE CHECK VARIABLES
		var hasIcons = 0;
		var facebookCheck = 'true';
		var twitterCheck = 'true';
		var youtubeCheck = 'true';
		var newsCheck = 'true';

		// SHOW THE ICONS THAT ARE NEEDED
	    	if(facebookCheck == "true"){
		        var facebookTarget = 'New Window';
		        if(facebookTarget == "New Window"){
		        	facebookTarget = "_blank";
		        } else {
		        	facebookTarget = "_self";
		        }
		        $(".icn.facebook").removeClass("hidden").attr("target", facebookTarget);
			hasIcons = 1;
		}
	    	if(twitterCheck == "true"){
		        var twitterTarget = 'New Window';
		        if(twitterTarget == "New Window"){
		        	twitterTarget = "_blank";
		        } else {
		        	twitterTarget = "_self";
		        }
		        $(".icn.twitter").removeClass("hidden").attr("target", twitterTarget);
		        hasIcons = 1;
		}
		if(youtubeCheck == "true"){
		        var youtubeTarget = 'New Window';
		        if(youtubeTarget == "New Window"){
		        	youtubeTarget = "_blank";
		        } else {
		        	youtubeTarget = "_self";
		        }
		        $(".icn.youtube").removeClass("hidden").attr("target", youtubeTarget);
		        hasIcons = 1;
		}
        //this is now for the Instagram icon
		if(newsCheck == "true"){
		        var newsTarget = 'New Window';
		        if(newsTarget == "New Window"){
		        	newsTarget = "_blank";
		        } else {
		        	newsTarget = "_self";
		        }
		        $(".icn.instagram").removeClass("hidden").attr("target", newsTarget);
		        hasIcons = 1;
		}
	}

    function accessibility() {
    	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };


        $(".sw-mystart-dropdown.translate").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();
                    //$(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");

                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                      if(!$(this).hasClass("open")) {
                          // TOGGLE THE DROPDOWN

                          $(".sw-dropdown", this).slideDown("slow", "swing").attr("aria-hidden", "false");
                          $(".selector", this).attr("aria-expanded","true");
                          $(this).addClass("open");
                          $(".sw-dropdown-list li:first-child a", this).focus();

                      } else {

                          $(".sw-dropdown", this).slideUp("slow", "swing").attr("aria-hidden", "true");
                          $(this).removeClass("open");
                          $(".selector", this).attr("aria-expanded","false").focus();
                      }
                      //$(".sw-dropdown-list li a", this).attr("tabindex","0");
                    // FOCUS FIRST ITEM
                    //$(".sw-dropdown-list li:first-child a", this).focus();
                break;
            }
        });

        $(".sw-mystart-dropdown.translate .sw-dropdown-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME TAB KEY
                case csKeyCodes.tab:
                    e.preventDefault();

                    $(this).parent().parent().parent().slideUp("slow", "swing").attr("aria-hidden", "true");
                    $(this).parent().parent().parent().parent().removeClass("open");
                    $(this).parent().parent().parent().parent().find(".selector").attr("aria-expanded","false").focus();
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                break;
            }
        });

        $("#sw-myaccount").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();


                    if(!$(this).hasClass("open")) {
                    	$(this).next("#sw-myaccount-list").slideDown("slow", "swing").attr("aria-hidden","false").find("li:first-child a").focus();
                        $(this).attr("aria-expanded","true").addClass("open");
                    } else {
                    	$(this).next("#sw-myaccount-list").slideUp("slow", "swing").attr("aria-hidden", "true");
                        $(this).attr("aria-expanded","false").removeClass("open").focus();
                        $(this).parent().removeClass("clicked-state");
                    }



                    // FOCUS FIRST ITEM
                    //$(this).next("#sw-myaccount-list").find("li:first-child a").focus();
                break;
            }
        });

        $("#sw-myaccount-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest("#sw-myaccount-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest("#sw-myaccount-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest("#sw-myaccount-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest("#sw-myaccount-list").find("li:last-child > a").focus();
                break;
            }
        });


        $("#gb-mystart .sw-mystart-dropdown.schoollist").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();

                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                      if(!$(this).hasClass("open")) {
                          $("#gb-schoollist-outer").slideDown("slow").attr("aria-hidden","false").addClass("open");
                          $(this).addClass("open").attr("aria-expanded","true");
                          $("#gb-schoollist-outer .gb-schoollist.elem > ul.col-one > li:first-child > a").focus();
                      } else {
                          $("#gb-schoollist-outer").slideUp("slow").attr("aria-hidden","true").removeClass("open");
                          $(this).removeClass("open").attr("aria-expanded","false").focus();
                      }
                break;
            }
        }); 

        $("#gb-schoollist li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().parent().hasClass("col-one") && $(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(".gb-schoollist.high li:last-child > a").focus();
                    } else if($(this).parent().parent().hasClass("col-two") && $(this).parent().is(":first-child")){
                    	$(".gb-schoollist.elem ul.col-one li:last-child > a").focus();
                    } else if($(this).parent().is(".gb-schoollist.middle ul li:first-child")){
                    	$(".gb-schoollist.elem ul.col-two li:last-child > a").focus();
                    } else if($(this).parent().is(".gb-schoollist.high ul.first li:first-child")){
                    	$(".gb-schoollist.middle ul li:last-child > a").focus();
                    } else if($(this).parent().is(".gb-schoollist.high ul.other li:first-child")){
                    	$(".gb-schoollist.high ul.first li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().hasClass("last-school")) {
                        // FOCUS FIRST ITEM
                        $(".gb-schoollist.elem ul.col-one li:first-child > a").focus();
                    } else if($(this).parent().parent().hasClass("col-one") && $(this).parent().is(":last-child")){
                    	$(".gb-schoollist.elem ul.col-two li:first-child > a").focus();  
                    } else if($(this).parent().parent().hasClass("col-two") && $(this).parent().is(":last-child")){
                    	$(".gb-schoollist.middle ul li:first-child > a").focus();  
                    } else if($(this).parent().is(".gb-schoollist.middle ul li:last-child")){
                    	$(".gb-schoollist.high ul.first li:first-child > a").focus();  
                    } else if($(this).parent().is(".gb-schoollist.high ul.first li:last-child")){
                    	$(".gb-schoollist.high ul.other li:first-child > a").focus();
                    } else if($(this).parent().is(".gb-schoollist.high ul.other li:last-child")){
                    	$(".gb-schoollist.elem ul.col.one li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME TAB KEY
                case csKeyCodes.tab:
                    e.preventDefault();

                    $("#gb-schoollist-outer").slideUp("slow").attr("aria-hidden","true").removeClass("open");
                    $("#gb-mystart .sw-mystart-dropdown.schoollist").removeClass("open").attr("aria-expanded","false").focus();

                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                   $(".gb-schoollist.elem ul.col-one li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();
					$(".gb-schoollist.high li:last-child > a").focus();
                    // FOCUS LAST ITEM

                break;
            }
        });

    }

