$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=PcqIW617PWU6ThY%2fPr7Mpw%3d%3d&SessionID=7af475ba-ad65-442c-8ea5-6110794e7239&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f24%2f2020+11%3a22%3a05+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.64.44';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
