    function kCode() {
        var a = -120;
        var img = document.createElement("img");
        $(img).attr("src", "http://redesign05.esvbeta.com/sysimages/Captainunderpants.png");
        $(img).css("position", "fixed");
        $(img).css("width", "250px");
        var h = (Math.sin(a * (Math.PI / 180)) * 90) - 30;
        var w = (Math.cos(a * (Math.PI / 180)) * 90) + 30;
        $(img).css("left", w + "vw");
        $(img).css("top", h + "vh");
        $(img).css("transform", "rotate(" + (180 - a + 120).toString() + "deg)");
        $(img).appendTo("body");

        var interval = setInterval(function () {
            var h = (Math.sin(-a * (Math.PI / 180)) * 90) - 30;
            var w = (Math.cos(a * (Math.PI / 180)) * 90) + 30;
            $(img).css("left", w + "vw");
            $(img).css("top", h + "vh");
            $(img).css("transform", "rotate(" + (180 - a + 120).toString() + "deg)");
            a += 1;
            if (a >= -20) {
                $(img).remove();
                clearInterval(interval);
            }
        }, 10);
    }
    $(document).konami({
        result: kCode
    });

