    function hpPlayPause() {
        var hpVideo = document.getElementById("hpVideo");
        if (hpVideo.paused) {
            hpVideo.play();
            $(".Videocontrols #playPause").removeClass("Paused");
        } else {
            hpVideo.pause();
            $(".Videocontrols #playPause").addClass("Paused");
        }
    }
    function audioChanger() {
        var hpVideo = document.getElementById("hpVideo");
        if (hpVideo.volume == 0) {
            hpVideo.volume = 1;
            $(".Videocontrols #sound").removeClass("Muted");
        } else {
            hpVideo.volume = 0;
            $(".Videocontrols #sound").addClass("Muted");
        }
    }
    function FullScreen() {
        var vid = document.getElementById("hpVideoMiddleContainer");
        var isInFullScreen = (document.fullscreenElement && document.fullscreenElement !== null) ||
		(document.webkitFullscreenElement && document.webkitFullscreenElement !== null) ||
		(document.mozFullScreenElement && document.mozFullScreenElement !== null) ||
		(document.msFullscreenElement && document.msFullscreenElement !== null);
        if (isInFullScreen) {
            $(".hpVideoOuterContainer").removeClass("Full");
            $(".hpVideoOuterContainer #fullscreen").removeClass("Expanded");
            if (vid.requestFullScreen) {
                document.exitFullscreen();
            } else if (vid.webkitRequestFullScreen) {
                document.webkitExitFullscreen();
            } else if (vid.mozRequestFullScreen) {
                document.mozCancelFullScreen();
            } else if (vid.msExitFullscreen) {
                document.msExitFullscreen();
            }
        } else {
            $(".hpVideoOuterContainer").addClass("Full");
            $(".hpVideoOuterContainer #fullscreen").addClass("Expanded");
            if (vid.requestFullScreen) {
                vid.requestFullScreen();
            } else if (vid.webkitRequestFullScreen) {
                vid.webkitRequestFullScreen();
            } else if (vid.mozRequestFullScreen) {
                vid.mozRequestFullScreen();
            } else if (vid.msRequestFullscreen) {
                vid.msRequestFullscreen();
            }
        }
    }
    $(document).on('input change', '#videoPosition', function () {
        var hpVideo = document.getElementById("hpVideo");
        var newTime = $("#videoPosition").val() * hpVideo.duration / 100;
        hpVideo.currentTime = newTime;
    });
    var videoHoverCounter = 0;
    setInterval(function () {
        var isInFullScreen = (document.fullscreenElement && document.fullscreenElement !== null) ||
		(document.webkitFullscreenElement && document.webkitFullscreenElement !== null) ||
		(document.mozFullScreenElement && document.mozFullScreenElement !== null) ||
		(document.msFullscreenElement && document.msFullscreenElement !== null);
        if (!isInFullScreen) {
            $(".hpVideoOuterContainer").removeClass("Full");
            $(".hpVideoOuterContainer #fullscreen").removeClass("Expanded");
        } else {
            $(".hpVideoOuterContainer").addClass("Full");
            $(".hpVideoOuterContainer #fullscreen").addClass("Expanded");
        }
        var hpVideo = document.getElementById("hpVideo");
        $("#videoPosition").val(hpVideo.currentTime * 100 / hpVideo.duration);
        if (videoHoverCounter > 0) {
            videoHoverCounter--;
            $(".hpVideoContainer").css("cursor", "");
            $(".Videocontrols").css("cursor", "");
        } else {
            $(".hpVideoContainer").removeClass("hover");
            $('.hpVideoContainer').css('cursor', 'none');
            $('.Videocontrols').css('cursor', 'none');
            $(".Videocontrols").removeClass("hover");
        }
    }, 10);
    $(document).ready(function () {
        hpPlayPause();
    });

