$(document).ready(function() {
			///////////////////////////////////////
		/////     SHOW/HIDE PREV/NEXT     ///// 
		/////////////////////////////////////// 


	    var rotateSpeed = 4000;
	    var fadeEffect = "slow";

	    var ImageList = $(".divMainCalloutSlideshowImageHolder .slidewrapper");
	    var TextList = $(".divMainCalloutSlideshowText");   
	    var LinkList = $(".anchSlideshowImageLink");
	    var numberOfSlides = ImageList.length;
	    var currentSlide = 1;
	    var timer;  

	    $(ImageList).hide();
	    $(TextList).hide();
	    $(LinkList).hide();

	    function showCurrentSlide(currentSlide, nextSlide){      
	        $(".Image" + currentSlide).fadeOut(fadeEffect);
	        var thisImageLink = $(".Link" + currentSlide);
	        if (thisImageLink.length > 0){
	            thisImageLink.hide();
	        }

	    	$(".Text" + currentSlide).fadeOut(fadeEffect);	        $("#Thumb" + currentSlide).removeClass('active');
	        $("#Thumb" + currentSlide).addClass('dormant');

	        $(".Image" + nextSlide).fadeIn(fadeEffect);
	        var nextImageLink = $(".Link" + nextSlide);
            if (nextImageLink.length > 0){
                nextImageLink.show();
            }

	    	$(".Text" + nextSlide).fadeIn(fadeEffect);	        $("#Thumb" + nextSlide).removeClass('dormant');
	        $("#Thumb" + nextSlide).addClass('active');

	        $("#currentSlide").val(nextSlide);
	    }

	    function startStopTimer(){
	        var method = $("#method").val();
	        var currentSlide = $("#currentSlide").val();    
	        if (method == 'forward'){
	            if (currentSlide == numberOfSlides){var nextSlide = 1;}
	            else {var nextSlide = parseInt(currentSlide) + 1;}
	        }
	        else if (method == 'backward'){
	            if (currentSlide == 1){var nextSlide = numberOfSlides;}
	            else {var nextSlide = currentSlide - 1;}
	        }
	        showCurrentSlide(currentSlide,nextSlide);  
	    }

	    showCurrentSlide($("#currentSlide").val(), currentSlide);   

	    $("#pauseButton").click(function(){
	        clearInterval(timer);
	        return false;
	    });
	    $("#playButton").click(function(){
	        $("#method").val('forward');
	        timer = setInterval(startStopTimer,rotateSpeed); 
	        return false;
	    });
	    $("#restartButton").click(function(){
	        $("#method").val('forward');
	        clearInterval(timer);
	        showCurrentSlide($("#currentSlide").val(), 1);
	        timer = setInterval(startStopTimer,rotateSpeed); 
	        return false;
	    });
	    $("#nextButton").click(function(){
	        $("#method").val('forward');
	        clearInterval(timer);
	        startStopTimer();
	        return false;
	    });
	    $("#prevButton").click(function(){
	        $("#method").val('backward');
	        clearInterval(timer);
	        startStopTimer();
	        return false;
	    });
	    $(".ThumbImage").click(function(){
	        $("#method").val('forward');
	        var thumbClicked = $(this).attr('id');
	        thumbClicked = thumbClicked.replace('Thumb','');
	        clearInterval(timer);
	        showCurrentSlide($("#currentSlide").val(), thumbClicked);
	    });


    // change these variables to whatever social media you want to showup in the footer of the image/gallery
    var pinterestWebsite = 'https://www.talawanda.org/pages//';
    var pinterestImageLink = 'https://www.talawanda.org/pages//image/';
    var pinterestDescription = '';  

    var socialmedia;
    socialmedia = '<div class="pp_social">';
    socialmedia = socialmedia + '<div class="twitter">';
    socialmedia = socialmedia + '<a href="http://twitter.com/share" class="twitter-share-button" data-count="none">Tweet</a>';  
    socialmedia = socialmedia + '<script type="text/javascript" src="http://platform.twitter.com/widgets.js"><\/script>';
    socialmedia = socialmedia + '</div>';
    socialmedia = socialmedia + '<div class="facebook">';
    socialmedia = socialmedia + '<iframe src="http://www.facebook.com/plugins/like.php?locale=en_US&href='+location.href+'&amp;layout=button_count&amp;show_faces=true&amp;width=500&amp;action=like&amp;font&amp;colorscheme=light&amp;height=23" scrolling="no" frameborder="0" style="border:none; overflow:hidden; width:500px; height:23px;" allowTransparency="true"></iframe>';
    socialmedia = socialmedia + '</div>';
    socialmedia = socialmedia + '<div class="pinterest">';
    socialmedia = socialmedia + '<a target="_blank" href="http://pinterest.com/pin/create/button/?url'+pinterestWebsite+'&media='+pinterestImageLink+'&description='+pinterestDescription+'" class="pin-it-button" count-layout="horizontal">'; 
    socialmedia = socialmedia + '<img border="0" src="//assets.pinterest.com/images/PinExt.png" title="Pin It" />';
    socialmedia = socialmedia + '</a';
    socialmedia = socialmedia + '</div>';
    socialmedia = socialmedia + '</div>';


    $("a[rel^='imageGallery']").prettyPhoto({
        animation_speed: 'fast', /* fast/slow/normal */
        slideshow: false, /* false OR interval time in ms */
        autoplay_slideshow: false, /* true/false */
        opacity: 0.80, /* Value between 0 and 1 */
        show_title: true, /* true/false */
        allow_resize: true, /* Resize the photos bigger than viewport. true/false */
        default_width: 500,
        default_height: 344,
        counter_separator_label: '/', /* The separator for the gallery counter 1 "of" 2 */
        theme: 'pp_default', /* light_rounded / dark_rounded / light_square / dark_square / facebook */
        horizontal_padding: 20, /* The padding on each side of the picture */
        hideflash: false, /* Hides all the flash object on a page, set to TRUE if flash appears over prettyPhoto */
        wmode: 'opaque', /* Set the flash wmode attribute */
        autoplay: false, /* Automatically start videos: True/False */
        modal: false, /* If set to true, only the close button will close the window */
        deeplinking: true, /* Allow prettyPhoto to update the url to enable deeplinking. */
        overlay_gallery: true, /* If set to true, a gallery will overlay the fullscreen image on mouse over */
        keyboard_shortcuts: true, /* Set to false if you open forms inside prettyPhoto */
        ie6_fallback: true,
        social_tools: socialmedia
        });

	$("a.anchEventMoreInfo").prettyPhoto({
		 animation_speed: 'fast', /* fast/slow/normal */
		 social_tools: ''
	});


});
