/* //banner //alerts //session */
    $('.anchBannerAlertClose').on('click',function(e){
        e.preventDefault();
        var $thisAlert = $(this).attr('id');
        var $thisAlertContainer = $(this).closest('.divBannerAlertMessage');
        $thisAlert = $thisAlert.replace('alert-','');
        $.ajax({
            type:'POST',
            async:true,
            url: 'https://www.wayne-local.com/banner-alert-session.php',
            data:{
                alertID: $thisAlert
            },
            success:function(data){
                $thisAlertContainer.slideUp();
            }
        });
    });

