    var column1Height = $(".blogpullwrapper").height();
    var column2Height = $(".outerEvents").height();
    
    var maxHeight = 0;
    if (column2Height <= column1Height) {
    maxHeight = column1Height; 
    $(".blogpullwrapper").css('height', maxHeight+100);
    $(".outerEvents").css('height', maxHeight+100);
    $(".eventsheight").css('height', maxHeight-100);
    $(".eventsouterheight").css('height', maxHeight-100); 
    }
    
    
    if (column2Height >= column1Height) {
    maxHeight = column1Height;
    $(".blogpullwrapper").css('height', maxHeight+100);
    $(".outerEvents").css('height', maxHeight);
    $(".eventsheight").css('height', maxHeight-100);
    $(".eventsouterheight").css('height', maxHeight-100);
