div.script:
$(document).ready(function () {
    $("#9669f1e9-d894-42ce-8669-107bf5cd2095").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#cf0e1f6e-e0fb-4b26-9751-6b974065ad1a").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#cf0e1f6e-e0fb-4b26-9751-6b974065ad1a').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#cf0e1f6e-e0fb-4b26-9751-6b974065ad1a').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 7 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#0f673bcb-c57a-4284-bb82-423894484432').hide();
    jQuery('#0f673bcb-c57a-4284-bb82-423894484432').listSolrResults({
        currentPagePath: '/content/schools/isok/en',
        label: 'Upcoming Events',
        listFrom: 'solr',
        limit: '5',
        listType: 'event',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/content/schools/isok/en/calendar.html',
        viewAllTitle: 'View All',
        uuid: '0f673bcb-c57a-4284-bb82-423894484432',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

