div.script:
$(document).ready(function () {
    $("#3658482f-6bc7-4dbd-9e6d-e853f35295f2").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 4 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#04d4d90d-10cb-45bf-90da-1ea6a5c241b1").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#04d4d90d-10cb-45bf-90da-1ea6a5c241b1').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#04d4d90d-10cb-45bf-90da-1ea6a5c241b1').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 6 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#6d774410-2bcf-42f9-8a09-0ea7fd1db00d').hide();
    jQuery('#6d774410-2bcf-42f9-8a09-0ea7fd1db00d').listSolrResults({
        currentPagePath: '/content/schools/ovca/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '6d774410-2bcf-42f9-8a09-0ea7fd1db00d',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

