div.script:
if(document.viewport) {
    // attach the screen dimensions to the document
    document.viewPortWidth = document.viewport.getWidth();
    document.viewPortHeight = document.viewport.getHeight();
    // corrects the ie 7 viewport showing having no height or width
    if (document.viewPortWidth == 0 || document.viewPortHeight == 0) {
        document.viewPortWidth = document.body.clientWidth;
        document.viewPortHeight = document.body.clientHeight;
    }
    
    // adapt to the window resize
    Event.observe(window, "resize", function() {
        document.viewPortWidth = document.viewport.getWidth();
        document.viewPortHeight = document.viewport.getHeight();
        // corrects the ie 7 viewport showing having no height or width
        if (document.viewPortWidth == 0 || document.viewPortHeight == 0) {
            document.viewPortWidth = document.body.clientWidth;
            document.viewPortHeight = document.body.clientHeight;
        }
    });
}

