var SlideShow7784811initilized = false;
var SlideShow7784811 = new function() { };
LCMS.jq.extend(SlideShow7784811, slideShowClass);
//initialize the containers to that hold the previous, current and next images.
SlideShow7784811.autoTransitionWait = 8;
SlideShow7784811.container = LCMS.jq("#main_4_4_ctl00_SlideShowImageContent");
SlideShow7784811.currentContentContainer = LCMS.jq("#main_4_4_ctl00_current");
SlideShow7784811.nextContentContainer = LCMS.jq("#main_4_4_ctl00_next");
SlideShow7784811.previousContentContainer = LCMS.jq("#main_4_4_ctl00_previous");
SlideShow7784811.selectedContentContainer = LCMS.jq("#main_4_4_ctl00_selected");
//initialization function to set up the slideshow.  Once initialized the js will start the timer.

//set the height and the width of the container that contains the image and the caption so that the caption appears attached to the image
SlideShow7784811.resizeCaptionContainer = function() {
    var $img = LCMS.jq(this);
	$img.parents('.captionContainer').eq(0).width($img[0].width).height($img[0].height);
};

SlideShow7784811.setContainers = function(current_container) {
	//set up the containers for the next transition
	if (current_container === undefined) current_container = 'index';
	
	switch (current_container) {
		case 'next':
			SlideShow7784811.currentContentContainer.html(SlideShow7784811.nextContentContainer.html());
		break;
		case 'prev':
			SlideShow7784811.currentContentContainer.html(SlideShow7784811.previousContentContainer.html());
		break;
        case 'selected':
			SlideShow7784811.currentContentContainer.html(SlideShow7784811.selectedContentContainer.html());
		break;
		default:
			SlideShow7784811.currentContentContainer.html(SlideShow7784811.contents[SlideShow7784811.currentContentIndex]);
	}
    SlideShow7784811.nextContentContainer.html(SlideShow7784811.contents[SlideShow7784811.NextContentIndex(SlideShow7784811)]);
    SlideShow7784811.previousContentContainer.html(SlideShow7784811.contents[SlideShow7784811.PreviousContentIndex(SlideShow7784811)]);
};

SlideShow7784811.updateIndexes = function () {
    var currentIndex = SlideShow7784811.currentContentIndex;
    var photoCount = SlideShow7784811.container.parent().find('#SlideShow7784811IndexNavigation').data('photo-count');

    SlideShow7784811.container.parent().find('.sldw_photo_index.selected').removeClass('selected');
    SlideShow7784811.container.parent().find('.sldw_photo_index[data-index=' + currentIndex + ']').addClass('selected');
   
    if (photoCount > 7) {
        var maxIndex = SlideShow7784811.CalcMaxIndex(photoCount);
        var minIndex = SlideShow7784811.CalcMinIndex(photoCount);
        SlideShow7784811.container.parent().find('.sldw_photo_index').each(function() {
            var $li = LCMS.jq(this);
            var photoIndex = parseInt($li.data('index'));
            if ((photoIndex < minIndex) || (photoIndex > maxIndex)) {
                $li.hide();
            }
            else {
                $li.show();
            }
            
            if (photoIndex === maxIndex && maxIndex !== (photoCount - 1)) {
                $li.find('a').text('...');
            }
            else if (photoIndex === minIndex && minIndex !== 0) {
                $li.find('a').text('...');
            }
            else {
                $li.find('a').text((photoIndex + 1));
            }
        });
    }
};

SlideShow7784811.InitializeTransition = function()
{
    if (SlideShow7784811initilized) return;
    SlideShow7784811initilized = true;
	//load the containers based on the currentContentIndex
    SlideShow7784811.setContainers();
    
    SlideShow7784811.container.append(SlideShow7784811.currentContentContainer.html());
    
    
    	
    	var current_caption_container = SlideShow7784811.container.children('.SlideShowImage').first().children('.captionContainer')

        //set the captionbox to resize on the image load event
        if (current_caption_container.children('img').length > 0) {
			current_caption_container.children('img').unbind('load.LCMSslideshow').bind('load.LCMSslideshow', SlideShow7784811.resizeCaptionContainer);
        } else {
        	current_caption_container.children().children('img').unbind('load.LCMSslideshow').bind('load.LCMSslideshow', SlideShow7784811.resizeCaptionContainer);
        }
}

// Perform transition is run after the timer expires or when the next/prev arrows are clicke
SlideShow7784811.PerformTransition = function(trans_dir) 
{
	if (SlideShow7784811.transitioning == true) return;    
    var is_fwd = (trans_dir === 'next');
    var is_selected_index = (trans_dir === 'selected');
    var next_img = '';

    if (!is_selected_index) {
        // next_img only references the correct element before setContainers is called!
        next_img = is_fwd ? SlideShow7784811.nextContentContainer : SlideShow7784811.previousContentContainer;
    } else {
        SlideShow7784811.selectedContentContainer.html(SlideShow7784811.contents[SlideShow7784811.currentContentIndex]);
        next_img = SlideShow7784811.selectedContentContainer;
    }

    var next_caption_container = next_img.find('.captionContainer');
	if (next_caption_container.children('img').length > 0) {
		next_caption_container.children('img').unbind('load.LCMSslideshow').bind('load.LCMSslideshow', SlideShow7784811.resizeCaptionContainer);
	} else {
		next_caption_container.children().children('img').unbind('load.LCMSslideshow').bind('load.LCMSslideshow', SlideShow7784811.resizeCaptionContainer);
	}

    SlideShow7784811.updateIndexes();
    
    
    
    
        next_img.css('display', 'block');
        if (next_img.children('.SlideShowImage').eq(0).children().children('img').length > 0) {
			next_img.children('.SlideShowImage').eq(0).children().children('img').first().each(SlideShow7784811.resizeCaptionContainer);
        } else {
        	next_img.children('.SlideShowImage').eq(0).children().children().children('img').first().each(SlideShow7784811.resizeCaptionContainer); 
        }


        
            
            var start = {};
            var end = {};
            
                start.direction = is_fwd ? 'left' : 'right';
                end.direction = is_fwd ? 'right' : 'left';
            

            //hide the current container
	        next_img.css('display', 'none');
	        var animateDiv = SlideShow7784811.container.children('.SlideShowImage').eq(0);
            animateDiv.hide('drop', start, 1 * 500, function() {                
                animateDiv.html(next_img.children(":first").clone(true));
	            next_img.css('display', 'block');
	            SlideShow7784811.container.parent().parent().height(next_img.height());
	            next_img.css('display', 'none');
	            SlideShow7784811.setContainers(trans_dir);
                animateDiv.show('drop', end, 1 * 500);
            });

        
}

function nextSlide7784811() {
	SlideShow7784811.MoveToNextSlide(SlideShow7784811, 'SlideShow7784811');
}
function previousSlide7784811() {
	SlideShow7784811.MoveToPreviousSlide(SlideShow7784811, 'SlideShow7784811');
}
function goToSlide7784811(index) {
    SlideShow7784811.MoveToSlide(SlideShow7784811, index);
}

