var SlideShow7784811Started = false;
LCMS.jq(function() {
    if (SlideShow7784811Started)
        return;
    SlideShow7784811Started = true;
	SlideShow7784811.SetContents(); 
	SlideShow7784811.StartSlideShow(SlideShow7784811, 'SlideShow7784811');
    LCMS.jq("#main_4_4_ctl00_divImagesMain")
        .mouseenter(function() {
		    LCMS.jq('#SlideShow7784811Navigation').css('visibility', 'visible');
		    SlideShow7784811.StopSlideShow(SlideShow7784811)
	    })
        .mouseleave(function() {
		    LCMS.jq('#SlideShow7784811Navigation').css('visibility', 'hidden');
		    SlideShow7784811.ResumeSlideShow(SlideShow7784811, 'SlideShow7784811');
	    });
	
		
	
       		var resize_timer = null;
       		
			LCMS.jq(window).bind('resize', function() {
				if (!resize_timer) { // timer to throttle how many times this fires on resize
					resize_timer = setTimeout(function() {
						
						var findCurrentMsize = function(img_src){
							var start_str = img_src.indexOf('?m='), first_amp = img_src.indexOf("&");
							return img_src.substring(start_str + 3, first_amp);
						};
						
						var	resizeM, 
							container_width = LCMS.jq('#main_4_4_ctl00_divImages').parent().width(),
							current_m_val = findCurrentMsize(LCMS.jq('.captionContainer img', SlideShow7784811.currentContentContainer).first().attr('src')) - 0; // - 0 to convert to number
						
						
						// only resize if our resize width doesn't match the container
						if (current_m_val !== container_width) {
							
							resizeM = function(img_src) {
								var start_str = img_src.indexOf('?m='), first_amp = img_src.indexOf("&");
								var old_str = img_src.substring(start_str, first_amp);
								return img_src.replace(old_str, '?m=' + container_width);
							};
							
							LCMS.jq('#main_4_4_ctl00_divImages').parent('.SlideShowContainer').css("height", "auto");
							LCMS.jq('#main_4_4_ctl00_divImages, #main_4_4_ctl00_divImages .captionContainer').removeAttr('style');
							LCMS.jq('#main_4_4_ctl00_divImages .SlideShowImage').css({'width': 'auto'});
							
							
							// resize the slideshow images currently showing
							LCMS.jq('#main_4_4_ctl00_divImages .captionContainer img').each(function() {
								var $ele = LCMS.jq(this);
								$ele.attr('src', resizeM($ele.prop('src'))).css({display: 'block'}); // set new src
								
								// manually fire load event, this is needed for some browsers that wont fire if the image gets cached. Some browsers will fire the event twice because of this but that's ok.
								if ($ele.prop('complete')) { $ele.trigger('load'); }													
							});
					
					
							// resize everything (in the array)
							LCMS.jq.each(SlideShow7784811.contents, function(indx, img_src) {
								SlideShow7784811.contents.splice(indx, 1, resizeM(img_src));	
							});

							
							LCMS.jq('#main_4_4_ctl00_divImages, #main_4_4_ctl00_divImages .SlideShowImage').css({'width': container_width});
							

							
						}

						resize_timer = null;				
					}, 300);	
				}				
			});        
    
});
