nav.script:
if (!('placeholder' in document.createElement('input'))) {
	var el = document.getElementById('q');
	var orig = 'Search this site';
	el.value = orig;

	el.onclick = function() {
		if (this.value==orig) this.value='';
	}

	el.onblur = function() {
		if (this.value=='') this.value=orig;
	}
}

section.script:
(function(d, s, id) {
									var js, fjs = d.getElementsByTagName(s)[0];
									if (d.getElementById(id)) return;
									js = d.createElement(s); js.id = id;
									js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.7";
									fjs.parentNode.insertBefore(js, fjs);
								}(document, 'script', 'facebook-jssdk'));

body.script:
document.getElementById('pslink').setAttribute('onclick', 'validatePass()');
function validatePass(){
    if(document.getElementById('password').value == 'weblinkwork'){
        window.open("https://registration.powerschool.com/family/gosnap.aspx?action=17291&culture=en", '_blank').focus();
        return true;
    }else{
        alert('Please try the password again.  If you do not know the password, visit the district office, or any school.  Thank you!');
        return false;
    }
}

window.onload = init;
var topnavButton = document.getElementById('sitenav_main_mobile_toggle');
var topnavDisplay = document.getElementById('sitenav_main');

function init() {
	topnavButton.onclick = toggleNav;
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
}
