   function addSchools(schoolName, schoolLink, schoolBefore){
        var s = schoolName.length;
        for(var i = 0; i < s; i++ ){
            if(schoolBefore[i] != ''){
                $('
'+ schoolName[i] +'').insertBefore($('.schoollist .sw-dropdown-list li:contains("'+ schoolBefore[i] +'")'));
            } else {
                $('
'+ schoolName[i] +'').appendTo($('.schoollist .sw-dropdown-list'));
            }
        }
    }
    addSchools(
