    //Sets search to custom google allows for results from subsites 
    $(function(){
		var searchID = "018342302013277717360:zcdr8tuajjq";
		$("#sw-search-button").click(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "//www.google.com/cse?cx="+searchID+"&hq=more:pagemap:document-siteid:4&ie=UTF-8&q="+$("#sw-search-input", searchForm).val()+"#gsc.tab=0&gsc.q="+$("#sw-search-input", searchForm).val()+"&gsc.sort=";
            e.stopImmediatePropagation();
		});
        $("#sw-search-input").keyup(function(e) {
          if (e.which == 13) {
          	e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "//www.google.com/cse?cx="+searchID+"&hq=more:pagemap:document-siteid:4&ie=UTF-8&q="+$("#sw-search-input", searchForm).val()+"#gsc.tab=0&gsc.q="+$("#sw-search-input", searchForm).val()+"&gsc.sort=";
            e.stopImmediatePropagation();
          }
        });
		$("#sw-search-input").focus(function() {
		    if($(this).val() == "Search") {
		        $(this).val("");
		    }
		});
		$("#sw-search-input").blur(function() {
		    if($(this).val() == "") {
		        $(this).val("Search");
		    }
		});
	});

    //09.29.2016 - Added by Jacinda 
    //Sets search to custom google allows for results from subsites 
    $(window).load(function() {
    	if($(window).width() < 1024) {
          var searchID = "018342302013277717360:zcdr8tuajjq";
          $("#rs-menu-search-button").click(function(e){
              e.preventDefault();
              var searchForm = $(this).parent();
              window.location.href = "//www.google.com/cse?cx="+searchID+"&hq=more:pagemap:document-siteid:4&ie=UTF-8&q="+$("#rs-menu-search-input", searchForm).val()+"#gsc.tab=0&gsc.q="+$("#rs-menu-search-input", searchForm).val()+"&gsc.sort=";
              e.stopImmediatePropagation();
          });
          $("#rs-menu-search-input").keyup(function(e) {
            if (e.which == 13) {
              e.preventDefault();
              var searchForm = $(this).parent();
              window.location.href = "//www.google.com/cse?cx="+searchID+"&hq=more:pagemap:document-siteid:4&ie=UTF-8&q="+$("#rs-menu-search-input", searchForm).val()+"#gsc.tab=0&gsc.q="+$("#rs-menu-search-input", searchForm).val()+"&gsc.sort=";
              e.stopImmediatePropagation();
            }
          });
          $("#rs-menu-search-input").focus(function() {
              if($(this).val() == "Search") {
                  $(this).val("");
              }
          });
          $("#rs-menu-search-input").blur(function() {
              if($(this).val() == "") {
                  $(this).val("Search");
              }
          });
        }
	});

