	function stickyHeader() {
		var scrolled = $(window).scrollTop();

		// MAKE ADJUSTMENTS IF TRANSLATE BAR IS IN USE
		if($("#hp-alert-outer").length) {
			var alertHeight = $("#hp-alert-outer").height();
			if(scrolled > alertHeight) {
				if($("body > .skiptranslate iframe").length && $(".skiptranslate").css("display") != "none") {
					$("#sw-mystart-outer").css({
						"position":"fixed", 
						"height":"35px", 
						"top":"40px", 
						"width":"100%"
					});
					$("#gb-links-outer").css("margin-top","39px");
				} else {
					$("#sw-mystart-outer").css({
						"position":"fixed", 
						"height":"35px", 
						"top":"0", 
						"width":"100%"
					});
					$("#gb-links-outer").css("margin-top","39px");
				}
			} else {
				if($("body > .skiptranslate iframe").length && $(".skiptranslate").css("display") != "none") {
					$("#sw-mystart-outer").css({
						"position":"relative", 
						"height":"35px", 
						"top":"auto", 
						"width":"100%"
					});
					$("#gb-links-outer").css("margin-top","0");
				} else {
					$("#sw-mystart-outer").css({
						"position":"relative", 
						"height":"35px", 
						"top":"auto", 
						"width":"100%"
					});
					$("#gb-links-outer").css("margin-top","0");
				}
			}
		} else {
			if(scrolled > 0) {
				if($("body > .skiptranslate iframe").length && $(".skiptranslate").css("display") != "none") {
					$("#sw-mystart-outer").css({
						"position":"fixed", 
						"height":"35px", 
						"top":"40px", 
						"width":"100%"
					});
					$("#gb-links-outer").css("margin-top","39px");
				} else {
					$("#sw-mystart-outer").css({
						"position":"fixed", 
						"height":"35px", 
						"top":"0", 
						"width":"100%"
					});
					$("#gb-links-outer").css("margin-top","39px");
				}
			} else {
				if($("body > .skiptranslate iframe").length && $(".skiptranslate").css("display") != "none") {
					$("#sw-mystart-outer").css({
						"position":"relative", 
						"height":"35px", 
						"top":"auto", 
						"width":"100%"
					});
					$("#gb-links-outer").css("margin-top","0");
				} else {
					$("#sw-mystart-outer").css({
						"position":"relative", 
						"height":"35px", 
						"top":"auto", 
						"width":"100%"
					});
					$("#gb-links-outer").css("margin-top","0");
				}
			}
		}




		// MAKE ADJUSTMENTS IF TRANSLATE BAR IS IN USE
		var channelBarClass = "fixed";
		if($("body > .skiptranslate iframe").length && $(".skiptranslate").css("display") != "none") {
			channelBarClass = "fixed translate";
		}

		if($(".hp").length) {
			var hpchannelTop = $(".hp #hp-content-outer").offset().top - 95;
			if(scrolled >= hpchannelTop) {
				$("#gb-channel-list-outer").addClass(channelBarClass);
				$("#hp-content-outer").addClass("padd-top");
			} else {
				$("#gb-channel-list-outer").removeClass(channelBarClass);
				$("#hp-content-outer").removeClass("padd-top");
			}
		} else {
			if(scrolled >= 117) {
				$("#gb-channel-list-outer").addClass(channelBarClass);
				$("#sp-content-outer, #spn-content-outer").addClass("padd-top");
			} else {
				$("#gb-channel-list-outer").removeClass(channelBarClass);
				$("#sp-content-outer, #spn-content-outer").removeClass("padd-top");
			}
		}

        if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
            if($(".wcm-controls").hasClass("wcm-stuck")) {
                $(".wcm-controls").css("margin-top", $("#sw-mystart-outer").outerHeight(true) + $("#gb-channel-list-outer").outerHeight(true) - 2);
            }
        }


		/*if(scrolled >= 35 && scrollLock == 0) {
			$("#sw-mystart-outer").addClass("fixed-header");
			$("body").css("padding", "35px 0 0 0");
