jQuery(document).ready(function($) {
	//if there is a notification, create space for it above the header
	function weglot_top() {
		var $weglot = $('.notification').css('height');

		if($('.notification').length) {
			h = $weglot.replace('px', '');
			$hOffset = Number(h) + 4 + 'px';
			$('.country-selector').css('top', $hOffset);
		}
	}

	weglot_top();
	$(window).resize(function() {
		weglot_top();
	});

});

