link.script:
jQuery(document).ready(function($) {

	$('.cycle-slideshow').cycle();

	$('marquee').marquee()

	$("#searchform input").focus(function(){
		$(this).animate({
			backgroundColor: "#FFF",
			color: "#000"
		}, 300 );
	});

	$("#searchform input").blur(function(){
		$(this).animate({
			backgroundColor: "#CCC",
			color: "#999"
	  }, 300 );
	});

	$('#footer-nav .sub-menu').toggle();

	$('#footer-nav ul.menu > li').each(function() {
		var listItem = $(this);
		if (listItem.children('ul').length > 0 ) {
			$(this).addClass('has-children');
		} else {
			$(this).addClass('no-children');
		}
	});


	$('#footer-nav ul.menu > li').click(function(){
		if ( $(this).children().length > 1 ) {
			var dropDown = $(this).children('ul');
			$(this).toggleClass('active');
			//$('ul.menu li > ul.sub-menu').not(dropDown).slideUp('slow');
			dropDown.slideToggle('slow');
		}
	});

});

div.script:
var slides = jQuery('.slideshow').children('div').length;
if(slides < 2) {
    jQuery('.cycle-pager').css("display", "none");
    jQuery('.cycle-prev').css("display", "none");
    jQuery('.cycle-next').css("display", "none");
} else {
    jQuery('.cycle-pager').css("display", "block");
}
jQuery('.slideshow').on('cycle-initialized', function (e, opts, API) {});
